INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82797, 1019, 'TEXT', 'examples.projects.1.subject', 'Suivi des populations d’oiseaux communs par échantillonnages réguliers le long de parcours prédéfinis', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82798, 1019, 'LINK', 'application.downloadLink', 'Je télécharge l’application INPN Espèces', null, null, 'https://inpn.mnhn.fr/informations/inpn-especes', false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82799, 1019, 'TEXT', 'examples.projects.1.description', 'Débuté en 2012 dans le cadre du Life+ CapDOM, conjointement avec la Martinique et La Réunion, le STOC-Guyane a pour but de suivre les variations d''abondances d''oiseaux communs sur le long terme pour évaluer l''impact des activités humaines. Le programme demande une implication régulière et nécessite une formation au protocole utilisé et la reconnaissance des espèces. A ce jour, près de cinquante parcours sont échantillonnés annuellement et au total, plus de 50 000 données ont été collectées.', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81893, 1001, 'TEXT', 'events.6.date', '1989', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81894, 1001, 'TEXT', 'events.4.description', 'Introduction du Raton-Laveur ([*Procyon lotor*](https://inpn.mnhn.fr/espece/cd_nom/60822)) en Guadeloupe depuis le continent américain', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81895, 1001, 'TEXT', 'events.5.description', 'Dernière éruption de la Soufrière', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81896, 1001, 'TEXT', 'interests.locations.2.name', 'La Réserve naturelle nationale des îles de Petite Terre', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81897, 1001, 'TEXT', 'ecosystems.ecosystems.3.description', 'En Guadeloupe, les mangroves se trouvent principalement en bordure du Grand et du Petit Cul-de-Sac marin et à Marie-Galante. On distingue 3 types de mangroves : la mangrove de bord de mer à la salinité constante est le territoire du Palétuvier rouge ([*Rhizophora mangle*](https://inpn.mnhn.fr/espece/cd_nom/447470)) ; la mangrove arbustive à une dizaine de mètres du rivage, où la salinité est extrême, où les Palétuviers noirs ([*Avicennia germinans*](https://inpn.mnhn.fr/espece/cd_nom/629127) et [*A. schaueriana*](https://inpn.mnhn.fr/espece/cd_nom/629128)) abondent ; la mangrove haute, au-delà, où l’on trouve Palétuviers blancs et gris ([*Laguncularia racemosa*](https://inpn.mnhn.fr/espece/cd_nom/630001) et [*Conocarpus erectus*](https://inpn.mnhn.fr/espece/cd_nom/629401)). On y trouve de nombreuses espèces de crabes et d’oiseaux. ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81898, 1001, 'TEXT', 'events.10.date', '2019', null, null, null, false);
