INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82802, 1019, 'TEXT', 'examples.projects.0.target', 'Plongeurs, apnéistes, pratiquant la photo sous-marine', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82803, 1019, 'TEXT', 'examples.projects.3.subject', 'Suivi de l’état de santé des récifs de Nouvelle-Calédonie', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82804, 1019, 'TEXT', 'header.subtitle', 'Enrichir ses propres connaissances et développer son sens de l’observation tout en participant à la progression et l''amélioration des connaissances scientifiques pour préserver la biodiversité, c’est le pari des sciences participatives.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82805, 1019, 'LINK', 'examples.projects.1.more', 'En savoir plus', null, null, 'http://www.gepog.org/Nos-actions/Milieux-terrestres/Suivi-Temporel-des-Oiseaux-Communs-STOC-EPS', false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82806, 1019, 'TEXT', 'examples.projects.2.title', 'TsiÔno, le réseau des observateurs du milieu marin mahorais', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82807, 1019, 'TEXT', 'header.title', 'C''est quoi les sciences participatives ?', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82808, 1019, 'IMAGE', 'examples.projects.2.image', null, 1344, 'Plongeur à Mayotte © Alexandra Gigou - OFB', null, false);
