INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82810, 1019, 'TEXT', 'paragraphs.1.title', 'Différents objectifs', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82811, 1019, 'TEXT', 'presentation.description', 'Le terme « sciences participatives » regroupe des programmes acquisition d’informations et de données scientifiques en impliquant une participation active des citoyens. Les observations sont réalisées de manière bénévole, le plus souvent par des non-professionnels. Dans certains cas, une formation au préalable est nécessaire, mais de nombreux programmes sont accessibles sans connaissances naturalistes poussées. ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82812, 1019, 'TEXT', 'examples.projects.3.actor', 'Association Pala Dalik', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82813, 1019, 'TEXT', 'paragraphs.2.text', 'Les équipes du Compteur et de [Vigie Nature](https://www.vigienature.fr/) ont mené conjointement une étude pour **dresser le premier panorama des programmes de sciences participatives dans les territoires ultramarins**. Consultez [la synthèse des résultats de cet état des lieux](media/article/Synthèse%20étude%20sciences%20participatives) et téléchargez [le rapport](https://inpn.mnhn.fr/docs-web/docs/download/398569).
