INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (35064, 1017, 'TEXT', 'presentation', 'Les activités humaines sont à l’origine du déclin de la biodiversité et la dégradation des écosystèmes. Aujourd’hui plus que jamais, il est nécessaire de remettre en question nos comportements et nos choix de consommation, au quotidien ou en voyage, chez soi ou en milieu naturel, pour mieux prendre soin de la biodiversité qui nous entoure. Petits efforts ou grands gestes, nous pouvons tous être acteurs du changement, pour limiter notre impact sur le monde qui nous entoure.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (35065, 1017, 'IMAGE', 'image', null, 1073, '© DR', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (35066, 1017, 'TEXT', 'quote', 'Des actions concrètes pour agir dès maintenant et préserver cette biodiversité les espèces et les écosystèmes', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (35067, 1017, 'TEXT', 'question', 'Qu’est-ce qu’un écogeste ? ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79915, 1048, 'TEXT', 'body.photos.3.description', '« Prise dans un filet de pêche, cette tortue verte ([*Chelonia mydas*](https://inpn.mnhn.fr/espece/cd_nom/77338)) a pu être secourue à temps. Les filets laissés sans surveillance sont de véritables pièges pour de nombreuses espèces qui ne sont pas ciblées par les pêcheurs. »', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79916, 1048, 'IMAGE', 'body.photos.1.photo', null, 1851, 'Raphaël Gailhac - OFB ', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79917, 1048, 'TEXT', 'body.photos.2.description', '« Les plages de Guyane comptent parmi les plus importants sites de pontes au monde pour les tortues marines. Ces deux jeunes tortues olivâtres ([*Lepidochelys olivacea*](https://inpn.mnhn.fr/espece/cd_nom/77360)) émergent de leur nid après une cinquantaine de jours d’incubation des œufs dans le sable. » ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79918, 1048, 'TEXT', 'body.photos.4.description', '« Nous posons un collier GPS sur ce jaguar ([*Panthera onca*](https://inpn.mnhn.fr/espece/cd_nom/443623)) capturé sur le terrain d’un agriculteur. Le suivi de ses déplacements nous permettra d’acquérir de meilleures connaissances sur l’espèce, notamment sur la taille de son domaine vital. »', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79919, 1048, 'CHECKBOX', 'presentation.shadowed', 'false', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79920, 1048, 'TEXT', 'body.photos.1.description', '« Pour lutter contre le braconnage des œufs et protéger les tortues marines, nous effectuons de nombreuses missions de surveillance sur les plages de ponte, ici sur la réserve de l’Amana. »', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79921, 1048, 'TEXT', 'body.photos.5.description', '« Après la pose du collier et une prise de sang, le jaguar est relâché en forêt, loin des habitations. L’espèce est accusée d’attaquer les animaux domestiques, en particulier les chiens, les ovins et les caprins. Des études sont réalisées pour mieux comprendre son comportement et permettre sa coexistence avec les activités humaines. » ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79922, 1048, 'TEXT', 'body.photos.7.description', '« La difficulté principale de nos missions repose sur l''étendue du territoire, avec seulement 11 inspecteurs de l''environnement pour une étendue grande comme le Portugal ! L''accès aux milieux amazoniens est très difficile et prend parfois plusieurs jours. Cette photo a été prise lors d''une patrouille sur le lac du barrage de Petit Saut sur la commune de Sinnamary. »', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79923, 1048, 'TEXT', 'presentation.name', 'Sur le terrain avec les agents de l’Office français de la biodiversité en Guyane', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81933, 1001, 'TEXT', 'species.4.description', 'Les Anolis sont des lézards communément observés sur tout le territoire. Ce terme regroupe plusieurs espèces et sous-espèces endémiques de la Guadeloupe et de ses dépendances, comme l''Anolis de Marie-Galante ([*Ctenonotus ferreus*](https://inpn.mnhn.fr/espece/cd_nom/654829)). Chaque île, voire presque chaque îlet des Antilles, possède une population d’anolis qui se différencie par ses couleurs et sa morphologie. Ils sont tous protégés.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79924, 1048, 'TEXT', 'body.photos.0.description', '« Cette photo a été prise en 2013, avant mon arrivée dans le service. Cet anaconda ([*Eunectes murinus*](https://inpn.mnhn.fr/espece/cd_nom/444031)) était détenu sans autorisation, il a été saisi puis relâché. La détention d’espèces sauvages est strictement réglementée pour protéger leurs populations. »', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79925, 1048, 'TEXT', 'body.photos.6.description', '« Je pose ici à côté de plusieurs dizaines de tortues charbonnières ([*Chelonoidis carbonarius*](https://inpn.mnhn.fr/espece/cd_nom/808589)) et denticulées ([*Chelonoidis denticulatus*](https://inpn.mnhn.fr/espece/cd_nom/808590)) saisies chez un commerçant. Ces espèces sont évaluées comme quasi-menacées sur la Liste rouge de l’Union Internationale pour la conservation de la nature établie pour la Guyane et leur commerce y est interdit. »', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79926, 1048, 'TEXT', 'body.photos.8.description', '« Ce qui me plaît le plus dans mes missions ? Contribuer à la protection de l''environnement, la diversité des missions, le relâché d''espèce sauvage suite à des saisies et la transmission des connaissances. Ici, une vue arienne d’une patrouille sur le lac du barrage de Petit Saut »', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79927, 1048, 'IMAGE', 'body.photos.6.photo', null, 1856, 'Raphaël Gailhac - OFB ', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79928, 1048, 'TEXT', 'presentation.author', '08/11/2021', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79929, 1048, 'IMAGE', 'body.photos.9.photo', null, 1859, 'Raphaël Gailhac - OFB ', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79930, 1048, 'TEXT', 'body.photos.9.description', '« J’ai la chance de vivre dans un département particulièrement riche en faune et flore et d’habiter en forêt. Les palmiers pinot ([*Euterpe oleracea*](https://inpn.mnhn.fr/espece/cd_nom/731659)) qui donnent du wassaï nourrissent une multitude d’espèce sur mon terrain, comme ce conure cuivré ([*Eupsittula pertinax*](https://inpn.mnhn.fr/espece/cd_nom/828571)), que j''ai pris en photo directement depuis ma terrasse. »', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79931, 1048, 'IMAGE', 'body.photos.4.photo', null, 1854, 'Raphaël Gailhac - OFB ', null, false);
