INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79939, 1048, 'TEXT', 'body.introduction', 'Raphael Gailhac est agent au service départemental de Guyane de l’Office français de la biodiversité depuis 2017. L’essentiel de son travail repose sur des missions de police de l’environnement : lutte contre le commerce et la détention illégales d''espèces, police de la chasse, assistance aux réserves naturelles, protection des milieux dans le cadre de la police de l''eau et infractions liées à la pêche maritime. Il nous emmène avec lui et ses collègues sur quelques-unes de leurs interventions. ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80893, 1049, 'TEXT', 'body.bodyElements.0.questionAnswer.question', 'Comment en êtes-vous venue à vous intéresser aux insectes lumineux ? ', null, null, null, false);
