INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80894, 1049, 'IMAGE', 'body.bodyElements.6.image.imageElement', null, 1900, 'Sortie nocturne d''observation des lucioles © Aline M. - An ba loup-la ', null, false);
