INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81945, 1001, 'TEXT', 'identity.presentation', 'Formée de deux îles principales, Basse-Terre et Grande-Terre, séparées par un bras de mer et entourées de plusieurs dépendances et d’îlets, la Guadeloupe, en raison de sa taille, son altitude, sa géologie et la diversité de ses paysages, est le territoire le plus riche en espèces, notamment endémiques, des Petites Antilles.  ', null, null, null, false);
