INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81969, 1001, 'IMAGE', 'interests.locations.1.image', null, 1007, 'Le lagon du Grand Cul-de-Sac Marin © Guilhem Pouxviel', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81970, 1001, 'IMAGE', 'ecosystems.ecosystems.1.image', null, 1458, 'Plage de Grande Anse © Amandine Escarguel - OFB', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (36712, 1029, 'TEXT', 'understand.text', 'On estime que seule 30% des espèces de faune et de flore présentes en outre-mer sont bien répertoriées. Les études pour suivre les espèces nécessitent beaucoup de temps d''observation, c’est pourquoi en multipliant les observateurs, on peut accumuler un plus grand nombre de données. De nombreux programmes de sciences participatives sont ainsi mis en place afin de permettre à chacun d''apporter sa pierre à l''édifice de la connaissance en faisant remonter ses observations. Certains programmes ont développé des applications mobiles accessibles directement depuis un smartphone.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (36713, 1029, 'IMAGE', 'action.cards.1.icon', null, 1149, 'réseau', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (36714, 1029, 'TEXT', 'presentation.category', 'Connaissance', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81971, 1001, 'TEXT', 'interests.locations.3.description', 'La réserve a été créée en 2011 pour préserver le patrimoine géologique exceptionnel de l’île de la Désirade, où l’on observe les très rares traces des prémices de la formation de la plaque tectonique des Caraïbes. L’île abrite également une végétation particulière avec plusieurs espèces rares dont le Cactus tête à l’Anglais ([*Melocactus intortus*](https://inpn.mnhn.fr/espece/cd_nom/447021)), ainsi que de nombreuses espèces d’oiseaux marins. ', null, null, null, false);
