INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (36716, 1029, 'TEXT', 'presentation.description', 'Participer à des actions concrètes pour faire progresser la connaissance et la préservation de la biodiversité, c’est possible !', null, null, null, false);
