INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81998, 1015, 'TEXT', 'ecosystems.ecosystems.3.description', 'Les herbiers sont composés principalement de phanérogames marines : ce ne sont pas des algues mais bien des plantes à fleurs aquatiques ! Ils sont des lieux de reproduction, de développement et d’alimentation de nombreuses espèces (poissons, oursins, tortues…). En Martinique, ils s’étendent sur les fonds sableux sur près de 5 000 ha.', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81999, 1015, 'TEXT', 'ecosystems.ecosystems.4.description', 'Des communautés coralliennes se développent sur tous les petits fonds de l’île mais elles ne forment de véritables récifs que sur la côte atlantique de l’île et dans la baie de Fort de France. 40 espèces coralliennes sont recensées dans les eaux de l’île, cependant, une importante partie des colonies sont considérées comme dégradées.', null, null, null, false);
