INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (1799, 1002, 'TEXT', 'description', 'Les Antilles sont un « point chaud » de la biodiversité mondiale. Si chacun des quatre territoires français du bassin a son identité et ses spécificités, les acteurs locaux collaborent autour de problématiques partagées. ', null, null, null, false);
