INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82061, 1015, 'IMAGE', 'ecosystems.ecosystems.2.image', null, 1278, 'Mangrove, Martinique © Fabien Lefebvre - Association ACWAA', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82062, 1015, 'TEXT', 'events.10.description', 'Publication de la Liste rouge de la Faune de Martinique', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82063, 1015, 'TEXT', 'interests.locations.0.description', 'À l''est de la Martinique, la presqu’île de la Caravelle présente une grande variété d’écosystèmes : savanes, falaises, forêt, mangroves. Le site est classé Réserve naturelle nationale depuis 1976 car il est le refuge du Moqueur gorge-blanche ([*Ramphocinclus brachyurus*](https://inpn.mnhn.fr/espece/cd_nom/442316)), oiseau endémique fortement menacé.  ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82064, 1015, 'TEXT', 'risks.risks.2.description', 'Face aux enjeux de la préservation et la valorisation de la biodiversité martiniquaise, 43 structures institutionnelles et associatives se sont réunies autour du Parc naturel régional de la Martinique pour la création d’un Observatoire Martiniquais de la biodiversité (OMB) afin de mutualiser les connaissances, sensibiliser le public et faciliter l’intégration de la biodiversité dans les politiques publiques.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82065, 1015, 'TEXT', 'risks.risks.1.description', 'Comme en Guadeloupe, la chlordécone, pesticide utilisé dans les bananeraies entre les années 1970 et 1990 a massivement pollué les milieux naturels de l’île. Si ses impacts sur la santé humaine commencent à être reconnus, ses conséquences sur les espèces et les écosystèmes sont également étudiées. Des études ont notamment montré une importante contamination des organismes aquatiques ce qui a conduit à l''interdiction de la pêche dans tous les cours d''eau et sur une partie des zones côtières de l''île.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82066, 1015, 'TEXT', 'interests.locations.2.description', 'Créé en 1976, le [Parc naturel régional](http://pnr-martinique.com/) s’étend sur 32 communes et couvre les 2/3 de l’île. Son objectif est de conjuguer le développement de l’île avec la préservation de ses richesses naturelles et culturelles. ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82067, 1015, 'TEXT', 'interests.locations.1.description', 'Ces deux zones de forêt humide au nord de l''île sont classées en Réserve biologique intégrale, gérées par l''Office national des forêts. La forêt y est laissée en évolution naturelle, l''exploitation et les interventions y sont interdites, à l''exception des aménagements pour la sécurité du  La Montagne Pelée, toujours en activité, est étroitement surveillée.', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82068, 1015, 'TEXT', 'ecosystems.ecosystems.3.name', 'Les herbiers sous-marins', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82069, 1015, 'TEXT', 'identity.subtitle', 'Des paysages contrastés', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82070, 1015, 'IMAGE', 'species.7.image', null, 1276, '*Ramphocinclus brachyurus* © Fabien Lefebvre - Association ACWAA', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82071, 1015, 'TEXT', 'species.5.name', 'L''Herbe à Tortue ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82072, 1015, 'TEXT', 'risks.risks.0.description', 'L’expansion démographique et l’urbanisation, surtout au centre et au sud de l’île, entraînent défrichements, rejets domestiques, industriels et agricoles, qui impactent fortement les milieux naturels de la Martinique. Les mangroves sont notamment des milieux particulièrement sensibles soumis à de fortes pressions anthropiques : aménagements, décharge, pollution, etc. Un projet de réserve naturelle régionale est à l’œuvre concernant les mangroves de la baie de Génipa.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82073, 1015, 'TEXT', 'ecosystems.ecosystems.2.description', 'A l’interface entre le milieu marin et le milieu terrestre, les mangroves ont des rôles fonctionnels très importants : protection contre l’érosion, piège à sédiment, puit de carbone, épuration de l''eau, nurserie pour poissons… Elles couvrent actuellement plus de 2 000 ha en Martinique, mais occupaient autrefois des surfaces beaucoup plus importantes avant d’être déboisées.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82074, 1015, 'TEXT', 'ecosystems.ecosystems.1.description', 'Les forêts humides se trouvent principalement dans le nord, sur les massifs montagneux et difficilement accessibles des pitons du Carbet de la Montagne Pelée. On distingue plusieurs types de forêts présentant différents cortèges floristiques en fonction de l’altitude. On y trouve une importante diversité, notamment en espèces endémiques. ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82075, 1015, 'TEXT', 'ecosystems.ecosystems.0.description', 'Couvrant un linéaire de 50 km sur l’île, surtout dans le sud, les plages sont des lieux de ponte pour 3 espèces de tortues marines et sont également bordées par un cortège floristique intéressant : Patate bord-de-mer([*Ipomoea pes-caprae*](https://inpn.mnhn.fr/espece/cd_nom/445581)), Pois bord-de-mer ([*Canavalia rosea*](https://inpn.mnhn.fr/espece/cd_nom/447163)), Raisinier bord-de-mer ([*Coccoloba uvifera*](https://inpn.mnhn.fr/espece/cd_nom/447455))… Cette végétation est toutefois fortement dégradée par les installations touristiques et l’importante fréquentation de certaines plages. ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82076, 1015, 'TEXT', 'identity.highestPoint', '1 397', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82077, 1015, 'TEXT', 'interests.locations.4.description', 'D’une superficie de 207 ha, ce site géré par le Conservatoire du littoral abrite une importante faune et flore patrimoniale. ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82078, 1015, 'TEXT', 'risks.risks.0.name', 'Une biodiversité fragile, soumise à de nombreuses pressions', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82079, 1015, 'IMAGE', 'interests.locations.1.image', null, 1059, 'Montagne Pelée © Youri Germany - Unsplash', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82080, 1015, 'IMAGE', 'ecosystems.ecosystems.1.image', null, 1268, 'Forêt humide de Martinique © Fabien Lefebvre - Association ACWAA', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82081, 1015, 'TEXT', 'interests.locations.3.description', 'Ces quatre îlets, classés Réserve naturelle nationale depuis 1995, sont l''un des plus importants sites de nidification des oiseaux marins des Petites Antilles. On y trouve entre autres puffins, sternes et noddis. L’accès en est strictement interdit.', null, null, null, false);
