INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (84070, 1057, 'TEXT', 'identity.area', '54', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (84071, 1057, 'TEXT', 'species.0.description', 'Neuf espèces de mammifères marins sont observables dans les eaux de Saint Martin, dont la Baleine à bosse ([Megaptera novaeangliae](https://inpn.mnhn.fr/espece/cd_nom/60867)). Mesurant entre 11 et 18 m de long, ce grand cétacé est reconnaissable à la bosse à l’avant de son aileron dorsal. Grandes migratrices, les baleines à bosse se nourrissent en été dans les mers froides et remontent vers les eaux chaudes pour se reproduire. Elles sont présentes dans les eaux de Saint Martin de janvier à mai, et la zone est le point de rencontre de deux sous-populations : celle fréquentant les Grandes Antilles et celle des Petites Antilles.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (84072, 1057, 'TEXT', 'identity.highestPointName', 'Pic Paradis', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (84073, 1057, 'TEXT', 'ecosystems.ecosystems.2.name', 'Les mangroves', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (84074, 1057, 'TEXT', 'species.7.description', 'Le Raisinier bord-de-mer ([*Coccoloba uvifera*](https://inpn.mnhn.fr/espece/cd_nom/447455)) est une plante arbustive que l’on trouve en bordure du rivage, qui peut atteindre 8 mètres de haut. Très résistant au sel et aux embruns, il possède des racines profondes qui retiennent le sable et limitent l’érosion du littoral. Il doit son nom de raisinier à ses fruits rappelant les grappes de raisin, qui peuvent être consommés crus ou utilisés dans des préparations.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (84075, 1057, 'TEXT', 'species.8.description', 'Le Gaïac ([*Guaiacum officinale*](https://inpn.mnhn.fr/espece/cd_nom/629786)) est un arbre endémique des Petites Antilles pouvant atteindre une douzaine de mètres de haut et dont les branches fourchues forment un couvert dense. Sa floraison et sa fructification sont colorées : il produit de belles fleurs bleues, ses fruits sont oranges et les graines enrobées d’une enveloppe rouge vif. On lui prête des vertus médicinales, qui lui valent également le nom de « bois de vie ».
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (84076, 1057, 'IMAGE', 'species.2.image', null, 1972, 'compléter', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (84077, 1057, 'TEXT', 'events.14.date', '2021', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (84078, 1057, 'TEXT', 'species.3.description', 'Le Mérou de Nassau ([*Epinephelus striatus*](https://inpn.mnhn.fr/espece/cd_nom/424158)) est un grand poisson de récifs reconnaissable à sa robe rayée, alternance de bandes irrégulières claires et brunes. Il peut atteindre plus d’un mètre de long et un poids de 25 kg. Autrefois abondant dans toute la Caraïbe, il est victime de pêche intensive et de la dégradation des habitats côtiers et est aujourd’hui considéré en danger critique d’extinction.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (84079, 1057, 'TEXT', 'statistics.3.number', '360', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (84080, 1057, 'TEXT', 'species.1.description', 'Parmi les 5 espèces fréquentant les eaux de Saint Martin, trois espèces de tortues marines (verte, imbriquée et Luth) viennent pondre sur le littoral et plus particulièrement sur les grandes plages de la côte orientale et les îlets. Entre 150 et 300 traces de pontes sont observées chaque année, les pontes de Tortue verte étant les plus nombreuses.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81600, 1013, 'SELECT', 'presentation.sourceSelect', 'inpn', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80912, 1049, 'TEXT', 'body.bodyElements.10.questionAnswer.response', 'Le programme est récent, on a démarré en 2019, il faut le temps que l’information circule. La plupart des gens qui passent du temps dans la nature n’ont pas forcément le réflexe ou l’intérêt de venir signaler les petites bêtes, mais le nombre d’observations augmente petit à petit. Certaines personnes ne sont pas très précises sur les informations concernant le lieu, ils ont peut-être peur qu’on débarque dans leur jardin ! On les rassure, ce n’est pas du tout le cas.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80913, 1049, 'IMAGE', 'presentation.image', null, 1895, 'A la découverte des lucioles de Guadeloupe et de leur milieu de vie © Aline M. - An ba loup-la ', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80914, 1049, 'TEXT', 'body.bodyElements.9.questionAnswer.question', 'Quelles sont les autres causes de la disparition des insectes lumineux ? Pourquoi on observe-t-on moins qu’avant ?', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80915, 1049, 'TEXT', 'body.bodyElements.5.questionAnswer.question', 'Comment peut-on participer à la connaissance des insectes lumineux en Guadeloupe ?', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80916, 1049, 'TEXT', 'body.bodyElements.8.citation.citationElement', 'Les lumières extérieures perturbent leur communication, les adultes ne peuvent pas se retrouver avec leurs signaux lumineux et ne peuvent donc pas se reproduire. ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80917, 1049, 'IMAGE', 'interviewee.photo', null, 1896, 'Logo Ti bet a limiè', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80918, 1049, 'TEXT', 'body.introduction', 'Depuis quelques années, la structure guadeloupéenne *An ba loup-la* (« sous la loupe » en créole antillais) anime le programme *Ti bet a limiè*, l’Observatoire des lucioles de Guadeloupe. L’objectif ? Recueillir des données sur ces insectes méconnus, mais aussi sensibiliser le public au rôle écologique des insectes et aux perturbations des milieux dont ils sont victimes. Rencontre avec Aline, la créatrice du projet. ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80919, 1049, 'TEXT', 'body.bodyElements.7.questionAnswer.response', '<strong>Une espèce bioindicatrice, c’est une espèce qui peut nous renseigner sur l’état de l’environnement</strong>&nbsp;: par exemple si on sait que telle espèce est sensible à la pollution du sol, on sait que si on la trouve à un endroit, ça veut dire que le sol y est peu pollué.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80920, 1049, 'TEXT', 'body.bodyElements.1.citation.citationElement', 'Il n’y a malheureusement pas de données historiques sur l’évolution des populations de ces insectes. ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81601, 1013, 'TEXT', 'understand.text2', 'La taxonomie, ou taxinomie, est la science qui permet de décrire et de classer le vivant. L’espèce est l’unité taxonomique de base. Chaque espèce décrite est classée avec d’autres espèces aux caractéristiques communes au sein d’un même genre, puis d’une famille, d’un ordre, etc. Chaque espèce est identifiée par un nom scientifique latin en deux mots, par exemple [*Pelecanus occidentalis*](https://inpn.mnhn.fr/espece/cd_nom/442189) pour le Pélican brun. Cette dénomination en latin permet un référentiel mondial commun, car une même espèce peut posséder des dizaines de noms usuels différents (dits noms «&nbsp;vernaculaires&nbsp;») à travers le monde, selon les langues et les régions. ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81602, 1013, 'TEXT', 'understand.text1', 'Plus de 80 % des nouvelles découvertes d''espèces chaque année en France sont réalisées dans les territoires d''outre-mer. Certaines de ces nouvelles descriptions sont issues d’expéditions scientifiques, par exemple celles menées en Guyane en 2014 et en Nouvelle-Calédonie entre 2016 et 2019 par le Muséum national d’Histoire naturelle dans le cadre de la campagne « [la Planète revisitée](https://www.mnhn.fr/fr/recherche-expertise/lieux/planete-revisitee) », mais d’autres sont également réalisées par des taxonomistes non-professionnels. Il peut s''agir également d''espèces déjà décrites dans des territoires frontaliers mais dont la présence n''avait pas encore été observée sur le territoire national. Dans la plupart des cas, il s’agit de petits organismes invertébrés et d’organismes marins (insectes, mollusques, crustacés, vers marins).
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81603, 1013, 'TEXT', 'territories.title', 'Nouvelles espèces décrites par an', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81604, 1013, 'CHECKBOX', 'presentation.shadowed', 'false', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81605, 1013, 'TEXT', 'visual.subtitle', 'Visualisez l''évolution du nombre de nouvelles espèces décrites', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81606, 1013, 'TEXT', 'understand.title1', 'L''outre-mer : un vivier d''espèces à découvrir', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81607, 1013, 'TEXT', 'understand.paragraphs.1.text', 'Les estimations actuelles du nombre d''espèces présentes sur Terre oscillent entre 8 et 10 millions (hors bactéries), et seules un peu plus de 2 millions d''espèces ont été décrites à ce jour. A l’échelle mondiale, environ 20 000 nouvelles espèces sont décrites chaque année. Cette tâche titanesque est complexifiée par le manque de spécialistes qui s’y consacrent, en particulier chez les invertébrés.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81608, 1013, 'TEXT', 'understand.title2', 'La taxonomie : la science de la description du vivant', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81609, 1013, 'TEXT', 'presentation.descriptionTerritories', 'nouvelles espèces sont décrites par an', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81610, 1013, 'TEXT', 'understand.paragraphs.0.title', 'Quelques exemples de découvertes récentes', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81611, 1013, 'TEXT', 'understand.keyword', 'especes', null, null, null, false);
