INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80922, 1045, 'TEXT', 'events.3.description', 'Naufrage de l''*Utile* à Tromelin', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80923, 1045, 'IMAGE', 'species.2.image', null, 1818, '*Chelonia mydas* - Tortue verte © Maxime Amy - TAAF', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80924, 1045, 'IMAGE', 'identity.image', null, 1819, 'Couple de fous à pieds rouges © Nelly Gravier - TAAF', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80925, 1045, 'TEXT', 'species.3.description', 'On trouve sur les différentes îles Éparses quinze espèces de reptiles terrestres. Il s''agit de scinques (lézards de la famille des Scincidés) et de geckos (famille des Gekkonidés). Certaines espèces ont probablement été introduites, mais cinq sont endémiques : deux d''Europa, deux de Juan de Nova et une aux Glorieuses. Ces populations fragiles sont menacées par les prédateurs introduits (chats, rats, souris).
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80926, 1045, 'TEXT', 'events.7.description', 'Classement d''Europa en site Ramsar', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80927, 1045, 'TEXT', 'statistics.3.number', '725 hectares', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80928, 1045, 'TEXT', 'events.7.date', '2011', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80929, 1045, 'TEXT', 'interests.locations.0.name', 'L''archipel des Glorieuses', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80930, 1045, 'TEXT', 'species.1.description', 'Le Filao ([*Casuarina equisetifolia*](https://inpn.mnhn.fr/espece/cd_nom/456837)) est un arbre aux rameaux filiforme qui pousse sur les sols sablonneux chargés en sel. Il peut atteindre une hauteur de 30 m, et bien qu''il ressemble à un pin, ce n''est pas un conifère. Ses fruits sont de petites billes formées de cônes à écailles pointues qui renferment les graines.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80931, 1045, 'TEXT', 'species.2.description', 'Cinq des sept espèces de tortues marines se retrouvent dans l’océan Indien et les îles Éparses sont des sites majeurs pour leur alimentation et leur reproduction pour deux d''entre elles, la Tortue verte ([*Chelonia mydas*](https://inpn.mnhn.fr/espece/cd_nom/77338)) et la Tortue imbriquée ([*Eretmochelys imbricata*](https://inpn.mnhn.fr/espece/cd_nom/77347)), en particulier Europa, où 2 000 à 10 000 femelles viennent pondre chaque année.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80932, 1045, 'TEXT', 'events.0.description', 'Découverte par Juan de Nova de l''île, d''abord baptisée *Agalega* (La Galicienne)', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80933, 1045, 'TEXT', 'events.1.description', 'Découverte probable d''Europa', null, null, null, false);
