INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80944, 1045, 'TEXT', 'species.2.name', 'Les Tortues marines', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80945, 1045, 'TEXT', 'species.4.description', 'Phaétons à brin rouge ou à bec jaune, Fou à pied rouge, Fou masqué, Frégate Ariel et Frégate Pacifique, sternes, puffins, noddis… il est difficile de résumer la diversité d’oiseaux marins qui peuplent les îles Éparses ! D’autant plus qu’on y trouve des sous-espèces endémiques. Pour certaines de ces espèces, Europa abrite les plus grandes colonies de l’océan Indien, c''est le cas par exemple pour la Sterne fuligineuse ([*Onychoprion fuscatus*](https://inpn.mnhn.fr/espece/cd_nom/528760)) dont les effectifs se comptent en millions d’individus.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80946, 1045, 'TEXT', 'events.6.description', 'Implantation d''une station météorologique permanente sur Tromelin', null, null, null, false);
