INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80954, 1045, 'TEXT', 'ecosystems.ecosystems.3.description', 'Aux Glorieuses, ces herbiers de phanérogames (plantes à fleurs) marines s’étendent sur une superficie de 36 km<sup>2</sup>. Ils représentent une zone d''alimentation, de refuge et de nurserie pour de nombreuses espèces, en particulier les tortues vertes et imbriquées.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80955, 1045, 'TEXT', 'statistics.3.text', 'de mangroves à Europa', null, null, null, false);
