INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80961, 1045, 'TEXT', 'events.2.date', '1751', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80962, 1045, 'TEXT', 'events.5.date', '1897', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80963, 1045, 'TEXT', 'identity.marineArea', '634 853', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80964, 1045, 'TEXT', 'events.8.date', '2012', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80965, 1045, 'TEXT', 'identity.presentation', 'Dans le canal du Mozambique, entre 10° et 25° de latitude sud, se répartissent l’archipel des Glorieuses, les îles Juan de Nova et Europa, et l’atoll de Bassas da India. Au nord de La Réunion, l’île de Tromelin complète cet ensemble de territoires français regroupés sous le nom d’îles Éparses, intégrés au sein des Terres australes et antarctiques françaises (TAAF).
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80966, 1045, 'IMAGE', 'interests.locations.0.image', null, 1811, 'Ile du Lys, archipel des Glorieuses © Hendrik Sauvignet - Office français de la biodiversité', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80967, 1045, 'TEXT', 'identity.population', '0', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80968, 1045, 'IMAGE', 'interests.locations.3.image', null, 1814, 'Vue aérienne d''Europa © TAAF', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80969, 1045, 'TEXT', 'species.3.name', 'Les reptiles terrestres', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (37512, 1024, 'IMAGE', 'action.cards.2.icon', null, 1124, 'relaché', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (37513, 1024, 'TEXT', 'presentation.name', 'Je protège les milieux contre l’introduction d’espèces exotiques envahissantes', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (37514, 1024, 'IMAGE', 'action.cards.0.icon', null, 1122, 'Planter', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (37515, 1024, 'IMAGE', 'presentation.image', null, 1472, 'La Tortue de Floride, l''une des espèces les plus envahissantes du monde © Amy Baugess', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (40369, 1006, 'TEXT', 'presentation.name', 'Je protège les récifs coralliens', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (40370, 1006, 'IMAGE', 'action.cards.0.icon', null, 1020, 'Bateau', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (40371, 1006, 'IMAGE', 'presentation.image', null, 1289, 'Eco-plongeur dans les récifs de Nouvelle-Calédonie © Jean-Pascal Quod / Reefcheck', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (40372, 1006, 'TEXT', 'understand.text', 'Les récifs coralliens sont un écosystème très riche : ils abritent plus d’un tiers des espèces marines connues pour lesquelles ils représentent notamment une zone de nourricerie pour les juvéniles (poissons, crustacés, etc.). Ils forment également une barrière naturelle contre la houle, protégeant la côte lors des tempêtes.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (40373, 1006, 'IMAGE', 'action.cards.1.icon', null, 1021, 'Crème solaire', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (40374, 1006, 'TEXT', 'action.cards.2.description', 'En plongée ou en snorkeling, je fais attention à ne pas toucher les coraux et je ne prélève rien. ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (40375, 1006, 'TEXT', 'presentation.category', 'En mer', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (40376, 1006, 'TEXT', 'action.cards.1.description', 'Je choisis une crème solaire moins nocive pour l’environnement', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (40377, 1006, 'TEXT', 'presentation.description', 'Les récifs coralliens sont soumis à de multiples pressions, et les activités de loisirs en mer (plaisance, plongée, pêche, baignade…) peuvent participer à leur dégradation.  Prenons soin de ces écosystèmes particulièrement riches, mais fragiles. ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (40378, 1006, 'TEXT', 'action.title', 'Pour profiter de la mer sans impacter les récifs', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80970, 1045, 'TEXT', 'risks.title', 'Enjeux et menaces ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80971, 1045, 'TEXT', 'species.0.name', 'Le Veloutier', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80972, 1045, 'TEXT', 'events.4.date', '1892', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80973, 1045, 'IMAGE', 'interests.locations.2.image', null, 1813, 'Vue aérienne de Juan de Nova © Stephanie Legeron - TAAF', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80974, 1045, 'IMAGE', 'ecosystems.ecosystems.2.image', null, 1889, 'Sansouïres d''Europa © B. Navez - Wikimedia commons', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80975, 1045, 'TEXT', 'interests.locations.3.name', 'Europa', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80976, 1045, 'TEXT', 'interests.locations.0.description', 'A l’entrée nord du canal du Mozambique, l’archipel regroupe quatre îles et rochers : Grande Glorieuse, l’île du Lys, les roches Vertes et le rocher du Sud. La biodiversité végétale est importante sur Grande Glorieuse, mais c’est surtout la richesse des récifs qui font des Glorieuses un point chaud de biodiversité, en particulier sur le banc du Geyser. L’ensemble des eaux de l’archipel a été classé en Parc naturel marin en 2012, puis en 2021 le Parc naturel marin a été remplacé par une Réserve naturelle nationale couvrant l’ensemble des îles de l’archipel et des eaux attenantes.', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (37516, 1024, 'TEXT', 'understand.text', 'Les espèces introduites par l’homme hors de leur territoire d’origine peuvent se développer de manière importante dans leur milieu d’introduction et [menacer la biodiversité locale](/indicateurs/especes-exotiques-envahissantes). Certaines espèces peuvent également avoir des conséquences économiques et sanitaires importantes.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (37517, 1024, 'IMAGE', 'action.cards.1.icon', null, 1396, 'aquarium', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (37518, 1024, 'TEXT', 'action.cards.2.description', 'Je ne relâche pas mes animaux de compagnie exotiques dans la nature. Certaines espèces sont désormais interdites de détention et nécessitent d’avoir des autorisations.  ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (37519, 1024, 'TEXT', 'presentation.category', 'Sur terre et en mer', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (37520, 1024, 'TEXT', 'action.cards.1.description', 'Je ne vidange pas mon aquarium dans la nature. Les plantes aquatiques et les poissons exotiques peuvent être dangereux pour l’équilibre des cours d’eau.', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (37521, 1024, 'TEXT', 'presentation.description', 'Les espèces exotiques envahissantes sont une menace majeure pour les écosystèmes, en particulier pour les espaces insulaires qui comptent de nombreuses espèces endémiques.', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (37522, 1024, 'TEXT', 'action.title', 'Pour limiter l’introduction et la dispersion d’espèces exotiques envahissantes ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80977, 1045, 'TEXT', 'interests.locations.2.description', 'Cette île de 6 km de long sur 1,6 km de large est entourée par une barrière corallienne délimitant un vaste lagon (200 km<sup>2</sup>). L’île a été fortement modifiée par l’exploitation du phosphate issu de la transformation du guano (fientes d’oiseaux), entre 1900 et 1972. Des habitations et des cultures y avaient été aménagées. Malgré l’impact de cette présence humaine, la biodiversité terrestre, en particulier la végétation, demeure riche.', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80978, 1045, 'TEXT', 'interests.locations.1.description', 'Cette petite île coralienne d’un kilomètre carré en forme d’amande est soumise à des conditions environnementales difficiles : nombreux cyclones, recouvrement par les vagues en cas de houle, forte salinité… C’est un lieu de ponte important pour les tortues vertes (entre 1 000 et 2 000 femelles par an).
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80979, 1045, 'TEXT', 'ecosystems.ecosystems.3.name', 'Les herbiers sous-marins', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80980, 1045, 'TEXT', 'statistics.1.text', 'de lagon à Europa', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (37523, 1024, 'TEXT', 'understand.quote', 'La Tortue de Floride, considérée comme l''une des 100 espèces les plus envahissantes du monde, est présente en milieu naturel dans quasiment tous les territoires d''outre-mer français (à l''exception de Saint-Pierre et Miquelon, Wallis et Futuna, et les TAAF)', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (37524, 1024, 'TEXT', 'understand.title', 'Comprendre l''écogeste', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (37525, 1024, 'TEXT', 'action.cards.0.description', 'Je me renseigne sur les espèces végétales que je souhaite planter dans mon jardin et je privilégie d’utiliser des espèces locales. Je respecte la règlementation sur l''interdiction d''introduction d''espèces sur mon territoire.', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80981, 1045, 'TEXT', 'events.9.date', '2021', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80982, 1045, 'TEXT', 'identity.subtitle', 'Une biodiversité remarquable sur des îles de petite taille', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80983, 1045, 'IMAGE', 'species.4.image', null, 1866, '*Onychoprion fuscatus* - Sterne fuligineuse © Maxime Amy - TAAF', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80984, 1045, 'TEXT', 'risks.risks.0.description', 'Insularité, isolement, faible occupation humaine, ces caractères permettent aux îles Éparses d’abriter une biodiversité remarquable au regard de leur petite taille, en particulier d’impressionnantes colonies d’oiseaux marins et des récifs coralliens dans un état de conservation quasiment intact. Cependant, elles ne sont pas exemptes d''impacts des activités humaines et sont soumises aux menaces des changements globaux.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80985, 1045, 'TEXT', 'events.3.date', '31 juillet 1761', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80986, 1045, 'TEXT', 'ecosystems.ecosystems.2.description', 'Ces habitats de zone humide qui couvrent 65 hectares sur les plaines autour du lagon et de la mangrove d’Europa sont composés d’une végétation adaptée aux milieux salins et qui se développe sur des sédiments d’origine corallienne. On y trouve notamment des salicornes ([*Caroxylon littorale*](https://inpn.mnhn.fr/espece/cd_nom/899430)).
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80987, 1045, 'IMAGE', 'ecosystems.ecosystems.3.image', null, 1888, 'Tortue verte au dessus d''un herbier de phanérogames marines © Hendrik Sauvignet - OFB', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80988, 1045, 'IMAGE', 'species.3.image', null, 1864, '*Cryptoblepharus caudatus* - Scinque aux yeux de serpent de Juan de Nova © Louis Dorémus - TAAF', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80989, 1045, 'IMAGE', 'zoom.map', null, 1917, 'Cartographie îles Eparses', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80990, 1045, 'TEXT', 'species.1.name', 'Le Filao', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80991, 1045, 'TEXT', 'interests.title', 'Les différentes îles et archipels', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80992, 1045, 'TEXT', 'ecosystems.ecosystems.1.description', 'Europa abrite les derniers reliquats de mangrove du sud-ouest de l''océan Indien dans un état de conservation exceptionnel. Cet écosystème primaire couvre 725 hectares autour du lagon. Il représente notamment un habitat favorable pour l''alimentation et le développement des tortues juvéniles et de certaines espèces de requins.', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80993, 1045, 'TEXT', 'ecosystems.ecosystems.0.description', 'Les îles Éparses totalisent près de 800 km² de superficies récifales, qu''on trouve autour des îles et sur certains monts sous-marins éloignés comme le banc du Geyser (dans la ZEE des Glorieuses). Ces récifs coralliens abritent une biodiversité riche et diversifiée, avec 3 656 espèces recensées à ce jour : coraux, poissons, éponges, mollusques, crustacés, échinodermes, etc.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80994, 1045, 'TEXT', 'identity.highestPoint', '14', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80995, 1045, 'TEXT', 'interests.locations.4.description', 'Cet atoll circulaire d’environ 10 km de diamètre est formé par un récif dont la quasi-totalité est immergée à marée haute, ce qui empêche tout développement terrestre. La richesse de Bassas da India se trouve donc dans ses eaux, elle est cependant mal connue en raison de ces conditions d’accès difficiles.  ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80996, 1045, 'TEXT', 'risks.risks.0.name', 'Des îles préservées, mais sous la pression des espèces introduites et des changements globaux', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80997, 1045, 'IMAGE', 'interests.locations.1.image', null, 1812, 'Vue aérienne de Tromelin © Nelly Gravier - TAAF', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80998, 1045, 'IMAGE', 'ecosystems.ecosystems.1.image', null, 1890, 'Tortue dans une mangrove à Europa © Bruno Marie - TAAF', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80999, 1045, 'TEXT', 'interests.locations.3.description', 'Cette île est la plus grande des îles Éparses (31&nbsp;km<sup>2</sup>). Elle est entourée de récifs frangeants, et abrite, au nord-est, un petit lagon entouré par une mangrove. Le reste de la végétation se divise entre une forêt sèche au nord et une plaine herbacée au sud. Europa présente l’avifaune marine la plus diversifiée des îles Éparses : 8 espèces nicheuses dont une sous-espèce endémique (le Phaéton à bec jaune d’Europa, [*Phaeton lepturus europae*](https://inpn.mnhn.fr/espece/cd_nom/785477)). C’est également le site de ponte de tortues vertes le plus important de l’océan Indien, avec plusieurs milliers de pontes par an.', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81000, 1045, 'TEXT', 'identity.title', 'Les îles Éparses,
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (84081, 1057, 'TEXT', 'species.2.description', 'Le Scinque d’Anguilla ([*Spondylurus powelli*](https://inpn.mnhn.fr/espece/cd_nom/649895) est un petit reptile dont la présence a été découverte en 2013 sur l’ilet Tintamarre. Endémique de la région du band d’Anguilla, il était jusqu’alors considéré présent uniquement à Anguilla et Saint Barthélemy. Ses découvreurs ont d’abord cru avoir retrouvé le Scinque de Saint Martin ([*Spondylurus martinae*](https://inpn.mnhn.fr/espece/cd_nom/649897) considéré éteint, avant que des analyses ADN viennent réfuter cette hypothèse.
