INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (84088, 1057, 'TEXT', 'species.4.description', 'Le Solenoptère de Chalumeau ([*Solenoptera chalumeaui*](https://inpn.mnhn.fr/espece/cd_nom/642561)) est un coléoptère endémique de Saint Martin. De couleur brune, il est couvert de soies grisâtres et peut mesurer jusqu’à 3 cm de long. Il se trouve uniquement autour du Pic Paradis, cette répartition extrêmement limitée rend l’espèce particulièrement vulnérable, notamment à la dégradation de son habitat.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (84089, 1057, 'TEXT', 'zoom.callToAction', 'Cliquez sur la carte thématique pour vous repérer en un coup d''œil ! ', null, null, null, false);
