INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (71987, 1043, 'TEXT', 'body.bodyElements.1.paragraph.paragraphText', '**En Guyane, une faune invertébrée abondante et peu documentée** : *15 000 espèces d’insectes sont connues en Guyane, mais les scientifiques estiment qu’il pourrait y en avoir plus de 80 000. Parmi les espèces d''insectes décrites, 650 sont des fourmis, mais 80&nbsp;% restent probablement à découvrir.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81616, 1013, 'TEXT', 'visual.title', 'La description de nouvelles espèces en outre-mer', null, null, null, false);
