INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (40379, 1006, 'TEXT', 'understand.quote', 'La grande barrière de corail de Nouvelle-Calédonie est la deuxième plus grande au monde, après celle se trouvant sur la côte est de l''Australie.', null, null, null, false);
