INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (84113, 1057, 'TEXT', 'interests.locations.0.description', 'La Réserve s’étend au nord-est de l’île. Sa partie marine couvre 2 796 hectares, permettant de protéger d’importants herbiers de phanérogames marines et de nombreuses formations coralliennes qui abritent une grande diversité d’espèces : mollusques, échinodermes, poissons, etc. La pêche y est interdite. La partie terrestre de 164 hectares couvre plusieurs iles et ilets, dont la partie littorale de Tintamarre et Pinel. Plus de 90 espèces d’oiseaux fréquentent la réserve pour nicher ou s’alimenter. ', null, null, null, false);
