INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (71988, 1043, 'TEXT', 'body.bodyElements.4.questionAnswer.question', 'Quelle relation ces jeunes entretiennent-ils avec la biodiversité commune, en particulier les fourmis et champignons ?', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81031, 1038, 'TEXT', 'ecosystems.ecosystems.3.description', 'Les savanes de Guyane occupent très peu d’espace : seulement 0,3% du territoire. Ce sont des milieux ouverts herbacés, avec quelques arbres ou arbustes plus ou moins isolés, qui se trouvent répartis sur une fine bande le long de la côte, entre la forêt et les zones de marais, sur les plaines basses de sédiments argilo-sableux. Malgré leur faible étendue, elles représentent un milieu remarquable pour la biodiversité : elles abritent 16 % de la flore guyanaise et la végétation y est très spécialisée.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81032, 1038, 'TEXT', 'ecosystems.ecosystems.4.description', 'De nombreuses zones humides se situent le long du trait de côte, en retrait des mangroves et en bordure des savanes. Des marais herbacés aux forêts marécageuses, on trouve une diversité de formations végétales qui abritent une faune importante, notamment des caïmans noirs, de nombreuses espèces de poissons d’eau douce ou saumâtre, des crustacés, de nombreux oiseaux en particulier des limicoles et des rapaces ou encore le remarquable Hoazin huppé ([*Opisthocomus hoazin*](https://inpn.mnhn.fr/espece/cd_nom/442155)).', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81033, 1038, 'TEXT', 'ecosystems.ecosystems.4.name', 'Les marais et les forêts marécageuses', null, null, null, false);
