INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73941, 1020, 'IMAGE', 'ecosystems.ecosystems.0.image', null, 1092, 'Forêt tropicale humide sur l’île de Lifou, Nouvelle-Calédonie © Benjamin Guichard / OFB', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73942, 1020, 'TEXT', 'species.11.description', 'Au cours de l’hiver austral, les baleines à bosse ([*Megaptera novaeangliae*](https://www.endemia.nc/faune/fiche5266)) viennent se reproduire en Nouvelle-Calédonie. L’archipel abrite ainsi une petite population qui fréquente les eaux du lagon mais également les monts sous-marins peu profonds de la zone économique exclusive. Des études scientifiques visent à mieux comprendre la migration des baleines depuis et vers l’archipel et à évaluer la connectivité au sein du parc et avec les populations voisines, dans l’objectif d’estimer le statut de cette population pour contribuer à sa conservation.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73943, 1020, 'TEXT', 'ecosystems.ecosystems.6.name', 'Les grandes algueraies à Sargasses', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73944, 1020, 'TEXT', 'species.12.description', 'Sur les sept espèces de tortues marines présentes dans le monde, trois fréquentent régulièrement les eaux calédoniennes : la Tortue Verte ([*Chelonia mydas*](https://endemia.nc/faune/fiche1127)), la Tortue imbriquée ([*Eretmochelys imbricata*](https://endemia.nc/faune/fiche1129)) et la Tortue grosse tête ou Tortue caouanne ([*Caretta caretta*](https://endemia.nc/faune/fiche1125))). Un plan d’action est en place depuis fin 2018, pour contribuer activement à la conservation des tortues marines et de leurs habitats, à l’échelle locale, régionale et internationale.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73945, 1020, 'TEXT', 'zoom.title', 'Quelques repères en Nouvelle-Calédonie', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73946, 1020, 'TEXT', 'species.8.name', 'Le Niaouli', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82105, 1025, 'IMAGE', 'presentation.file', null, 1469, 'affiche écogeste respect faune sauvage', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82106, 1025, 'CHECKBOX', 'presentation.shadowed', 'true', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82107, 1025, 'IMAGE', 'action.cards.2.icon', null, 1129, 'chien', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82108, 1025, 'IMAGE', 'action.cards.3.icon', null, 1130, 'véto', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82109, 1025, 'TEXT', 'presentation.name', 'Je respecte la faune sauvage', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82110, 1025, 'IMAGE', 'action.cards.0.icon', null, 1127, 'distance', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82111, 1025, 'IMAGE', 'presentation.image', null, 1126, 'Iguane endémique des Petites Antilles © Fabien Lefebvre - Association ACWAA', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82112, 1025, 'TEXT', 'understand.text', 'Observer la faune dans son milieu naturel est une chance et une expérience formidable. Cependant, sur terre comme en mer, déranger un animal peut le mettre en péril en occasionnant un stress important et une dépense d’énergie inutile. Il risque également d’abandonner son nid ou ses petits, se blesser, être plus facilement à la merci d’un prédateur…
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82113, 1025, 'IMAGE', 'action.cards.1.icon', null, 1128, 'bruits', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82114, 1025, 'TEXT', 'action.cards.3.description', 'Si un animal me parait en détresse, avant toute action, je contacte un professionnel (centre de soin, gestionnaires de réserve) afin de connaitre la démarche la plus adaptée pour lui porter secours.', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82115, 1025, 'TEXT', 'action.cards.2.description', 'Je tiens mon chien en laisse et je respecte la règlementation dans les lieux où sa présence n’est pas autorisée.', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82116, 1025, 'TEXT', 'presentation.category', 'Sur terre et en mer', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82117, 1025, 'TEXT', 'action.cards.1.description', 'Je limite les bruits (cris, musique…) pour que ma présence reste discrète.  ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82118, 1025, 'TEXT', 'presentation.description', 'Pour ne pas perturber les occupants des milieux naturels, mieux vaut se faire discret et ne pas laisser de traces de son passage.', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82119, 1025, 'TEXT', 'action.title', 'Pour profiter de la nature sans déranger ses occupants ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82120, 1025, 'TEXT', 'understand.quote', 'Une tortue en ponte est très sensible au dérangement, cela peut la conduire à abandonner son nid. ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82121, 1025, 'TEXT', 'understand.title', 'Comprendre l''écogeste', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82122, 1025, 'TEXT', 'action.cards.0.description', 'Je reste à distance des animaux sauvages que je rencontre et je ne cherche pas à les toucher', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (84127, 1057, 'TEXT', 'ecosystems.ecosystems.2.description', 'Peu étendues et clairsemée, les mangroves de Saint-Martin se limitent aux berges de certains étangs côtiers et couvrent 35 hectares au total. Elles sont formées de 4 espèces de palétuviers : [*Rhizophora mangle*](https://inpn.mnhn.fr/espece/cd_nom/447470), [*Laguncularia racemosa*](https://inpn.mnhn.fr/espece/cd_nom/630001), [*Conocarpus erectus*](https://inpn.mnhn.fr/espece/cd_nom/629401) et [*Avicennia germinans*](https://inpn.mnhn.fr/espece/cd_nom/629127). Leur superficie a drastiquement diminué au cours des dernières décennies à cause des défrichements et remblais au profil de l’urbanisation. Elles restent des écosystèmes importants pour l’épuration des eaux et la nidification des oiseaux. La mangrove de l’étang du cimetière de Grand Case abrite par exemple la seule colonie nicheuse de Grande Aigrette ([*Ardea alba*](https://inpn.mnhn.fr/espece/cd_nom/2504)) des Antilles françaises.', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (84128, 1057, 'TEXT', 'ecosystems.ecosystems.1.description', 'Contrairement aux récifs coralliens, les herbiers sous-marins couvrent de grandes étendues à Saint Martin. Leur superficie est estimée à 61,5 km<sup>2</sup> autour de l’ensemble de l’île. On y trouve 5 espèces de magnoliophytes marines, dont l’Herbe à tortue ([*Thalassia testudinum*](https://inpn.mnhn.fr/espece/cd_nom/627263)) et l’Herbe à lamentin ([*Syringodium filiforme*](https://inpn.mnhn.fr/espece/cd_nom/627259)). On y trouve également l’espèce exotique [*Halophila stipulaceae*](https://inpn.mnhn.fr/espece/cd_nom/368620), en expansion dans la Caraïbe bien que son système racinaire moins profond que les autres espèces semble la rendre plus sensible aux évènements climatiques. Ces herbiers, comme les fonds coralliens, abritent de nombreuses espèces d''invertébrés (étoiles de mer, oursins, langoustes, lambis...) et de poissons (mérou, chirurgien, perroquet, tarpon, barracuda, ange...). Ils sont la zone d’alimentation privilégiée des tortues vertes et autrefois du Lamentin ([*Trichechus manatus*](https://inpn.mnhn.fr/espece/cd_nom/443799)), aujourd’hui disparu dans les eaux des Antilles françaises et dont le dernier représentant en milieu naturel dans cette zone avait été photographié à Saint Martin.', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73947, 1020, 'TEXT', 'species.14.description', 'Les nautiles sont considérés comme des fossiles vivants dont les ancêtres étaient proches des ammonites, éteintes à la fin de l’ère secondaire. Ils sont les seuls céphalopodes à posséder une coquille, comme les escargots. Habitants du fonds des océans, ils ont une durée de vie d''une vingtaine d''années et se caractérisent par une croissance lente et un faible taux de reproduction.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73948, 1020, 'TEXT', 'events.6.description', 'Création du Parc marin de la mer de Corail ; Inscription des lacs du Grand Sud sur la liste des sites Ramsar', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73949, 1020, 'TEXT', 'species.13.description', 'Ces dernières décennies, la prospection biologique des milieux profonds notamment des monts sous-marins a permis la découverte de fossiles vivants. Parmi eux, un crinoïde ([*Neogymnochrinus richeri*](https://inpn.mnhn.fr/espece/cd_nom/647041)), de la famille des Sclerocrinidae,  datant du jurassique et supposée éteinte. Il a été retrouvé à la fin des années 80 sur la ride de Norflok.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73950, 1020, 'TEXT', 'species.15.description', 'Plus gros pigeon arboricole du monde, le Notou ([*Ducula goliath*](https://www.endemia.nc/faune/fiche71)) présente un plumage bleu gris, avec une pointe de pourpre sur les ailes, et ses yeux sont rouge vif. Il vit dans les forêts humides de la Grande Terre et contribue à sa régénération par dissémination des graines dont il se nourrit. Difficile à observer, il se repère toutefois facilement à son chant particulier, grave et sourd.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73951, 1020, 'TEXT', 'events.6.date', '2014', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73952, 1020, 'TEXT', 'events.4.description', 'Publication des Codes de l''environnement des Provinces Sud et Nord', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73953, 1020, 'TEXT', 'events.5.description', 'Création du [Conservatoire d''espaces naturels](https://www.cen.nc)', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73954, 1020, 'TEXT', 'interests.locations.2.name', 'La région des lacs du Grand Sud', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73955, 1020, 'TEXT', 'ecosystems.ecosystems.3.description', 'Aussi dit « sclérophylle », ce type de forêt n’est plus présent que sur environ 175 km<sup>2</sup>, soit moins de 1 % du territoire et moins de 2&nbsp;% de sa surface estimée d’origine. Localisées sur le littoral de la côte ouest, ces forêts sont très vulnérables, notamment aux aménagements agropastoraux, aux incendies et aux espèces exotiques envahissantes. Elles abritent une biodiversité élevée avec un taux d''endémisme de 60 %.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73956, 1020, 'TEXT', 'ecosystems.ecosystems.4.description', 'Forêt de bord de mer aux pieds dans l’eau, cette formation arborescente adaptée aux conditions de salinité des eaux, au manque d''oxygène et à l''instabilité des sols, est majoritairement située sur la côte ouest du fait de vastes plaines propices à son installation. Elle couvre 258 km<sup>2</sup> sur l''ensemble de l''archipel.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73957, 1020, 'TEXT', 'ecosystems.ecosystems.4.name', 'La mangrove', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73958, 1020, 'TEXT', 'ecosystems.ecosystems.5.description', 'Exposées aux activités humaines et vulnérables, ces prairies sous-marines occupent près de 940 km² de zones côtières peu profondes. On y compte 11 espèces de phanérogames marines parmi les 70 connues au niveau mondial.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73959, 1020, 'TEXT', 'ecosystems.ecosystems.7.name', 'Les fonds de baie envasés', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81624, 1007, 'TEXT', 'goal2.quote', '*Le Compteur constituera un signal d’alerte et donnera une visibilité forte et essentielle pour sensibiliser à l’évolution de la biodiversité.*  Livre bleu des outre-mer, 2018. ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81625, 1007, 'TEXT', 'goal1.title', 'Partager la connaissance scientifique', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (37446, 1026, 'IMAGE', 'presentation.file', null, 1470, 'affiche écogeste réduction consommation plastique', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (37447, 1026, 'CHECKBOX', 'presentation.shadowed', 'false', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (37448, 1026, 'IMAGE', 'action.cards.2.icon', null, 1133, 'jetable', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (37449, 1026, 'TEXT', 'presentation.name', 'Je réduis mes déchets et limite ma consommation de plastique', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (37450, 1026, 'IMAGE', 'action.cards.0.icon', null, 1131, 'sacs', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (37451, 1026, 'IMAGE', 'presentation.image', null, 1135, 'Jasmin Sessler / Unsplash', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (37452, 1026, 'TEXT', 'understand.text', 'Lorsqu’ils terminent dans la nature, nos déchets polluent durablement les milieux et nous finissons par les ingérer également au quotidien dans l’eau, l’air et la nourriture. Mais même lorsqu''ils sont collectés, leur impact sur l''environnement reste important : leur incinération génère une pollution de l''air et l''émission de gaz à effet de serre, l''enfouissement pollue durablement les sols et les eaux et s''avère rapidement problématique dans les petits territoires insulaires.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (37453, 1026, 'IMAGE', 'action.cards.1.icon', null, 1132, 'vrac', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (37454, 1026, 'TEXT', 'action.cards.2.description', 'Je fais la chasse aux produits à usages uniques : gobelets, pailles, touillettes, et je les remplace par des alternatives durables ou biodégradables.', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (37455, 1026, 'TEXT', 'presentation.category', 'Consommation', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (37456, 1026, 'TEXT', 'action.cards.1.description', 'Je privilégie les produits achetés en vrac. J''utilise mes propres contenants réutilisables. ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (37457, 1026, 'TEXT', 'presentation.description', 'Même lorsqu''ils sont collectés correctement, l''impact de nos déchets sur l''environnement reste important. ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82123, 1027, 'IMAGE', 'presentation.file', null, 1468, 'affiche écogeste préservation flore sauvage', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82124, 1027, 'CHECKBOX', 'presentation.shadowed', 'false', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82125, 1027, 'IMAGE', 'action.cards.2.icon', null, 1136, 'nettoyer', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82126, 1027, 'TEXT', 'presentation.name', 'Je préserve la flore sauvage  ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73960, 1020, 'TEXT', 'ecosystems.ecosystems.7.description', 'Le long du littoral de la Grande Terre se trouvent des baies protégées, aux fonds constitués de sédiments fins, souvent vaseux. Certains sont chargés en terre rouge provenant des sols naturellement délavés, d''autres ont une charge terrigène accrue par les activités humaines.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73961, 1020, 'IMAGE', 'species.14.image', null, 1328, '*Nautilus sp.* © Claude Payri', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (37458, 1026, 'TEXT', 'action.title', 'Pour commencer à réduire mes déchets : ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (37459, 1026, 'TEXT', 'understand.quote', 'A l’échelle mondiale, un tiers des déchets plastiques se retrouve dans la nature. Seuls 9% des déchets plastiques sont recyclés dans le monde.', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (37460, 1026, 'TEXT', 'understand.title', 'Comprendre l''écogeste', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (37461, 1026, 'TEXT', 'action.cards.0.description', 'Je refuse les sacs plastiques à usage unique, j''utilise des sacs réutilisables en tissus ou en matières naturelles.', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81068, 1038, 'TEXT', 'species.2.description', 'Sept espèces de toucan, oiseaux à grand bec de la famille des Ramphastidés, sont présentes en Guyane. Le Toucan ariel ([*Ramphastos vitellinus*](https://inpn.mnhn.fr/espece/cd_nom/442312)) et le Toucan à bec rouge ([*Ramphastos tucanus*](https://inpn.mnhn.fr/espece/cd_nom/442311)) sont communément observés, notamment en fin de saison des pluies, où ils viennent dans la partie côtière s’alimenter sur les palmiers en fruits. Le Toucan toco ([*Ramphastos toco*](https://inpn.mnhn.fr/espece/cd_nom/442310)) est le plus grand représentant de cette famille. Caractérisé par son bec orange, il est plus rare et observable uniquement au nord du département. C’est la seule espèce de toucan intégralement protégée.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82127, 1027, 'IMAGE', 'action.cards.0.icon', null, 1138, 'balisage', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (84129, 1057, 'TEXT', 'ecosystems.ecosystems.0.description', 'Les récifs coralliens sont peu développés autour de Saint Martin, et sont limités à la zone nord-est de l’île, en raison de la prédominance des fonds sableux dans les eaux peu profondes autour de l’île, qui favorise plutôt les herbiers sous-marins. Il est également possible que cette couverture plus réduite que dans les autres îles de l’arc des Petites Antilles soit due au passage plus fréquent des ouragans.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (84130, 1057, 'TEXT', 'identity.highestPoint', '424', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82128, 1027, 'IMAGE', 'presentation.image', null, 1140, '© Fabien Lefebvre - Association ACWAA', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (84131, 1057, 'TEXT', 'interests.locations.4.description', 'L’ile compte de nombreux étangs littoraux d’eau saumâtre, certains bordés par des mangroves. Historiquement exploités pour la production de sel, ils sont d’une grande importance pour l’avifaune mais également pour leur rôle d’épuration de l’eau et de régulation des flux hydriques. La plupart sont protégés (par arrêté de biotope, classement en site Ramsar ou acquis par le Conservatoire du littoral, et 2 sont inclus dans la Réserve Naturelle Nationale), mais ils restent fortement anthropisés et soumis à de nombreuses pressions actuelles : remblais, déversements de déchets, rejets d’eaux non traitées, défrichement de la mangrove.', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (84132, 1057, 'TEXT', 'risks.risks.0.name', 'Une île soumise à une forte pression anthropique ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (84133, 1057, 'IMAGE', 'interests.locations.1.image', null, 1985, 'compléter', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82129, 1027, 'TEXT', 'understand.text', 'Dans les territoires ultramarins, de nombreuses espèces de plantes sont endémiques et leur répartition est parfois limitée à une très petite zone. Piétinements ou cueillettes intensives peuvent menacer la survie de ces espèces végétales, déjà soumises à de plus importantes pressions : changements climatiques, défrichements pour créer des espaces agricoles, pâturages d’animaux domestiques, incendies…
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82130, 1027, 'IMAGE', 'action.cards.1.icon', null, 1137, 'protection', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82131, 1027, 'TEXT', 'action.cards.2.description', 'Je nettoie mes chaussures et mon matériel de randonnée régulièrement pour ne pas disséminer d’espèces envahissantes.', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82132, 1027, 'TEXT', 'presentation.category', 'Sur terre', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82133, 1027, 'TEXT', 'action.cards.1.description', 'Je ne cueille pas de plantes sauvages, il peut s’agir d’espèces fragiles et protégées. Si je souhaite prélever des plantes, je me renseigne d’abord sur la règlementation et les espèces protégées.', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82134, 1027, 'TEXT', 'presentation.description', 'Préserver la flore, c''est préserver l''équilibre des écosystèmes. ', null, null, null, false);
