INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (84135, 1057, 'TEXT', 'interests.locations.3.description', 'Situé dans la partie nord-est de Saint-Martin, Red Rock est l’un des derniers massifs boisés encore préservé de l’île. On y trouve différents types de milieux : forêt sèche, savanes, fourrés secs, zones humides, falaises, bordés par des plages de sables et de galets. Plus de 180 espèces végétales y ont été recensées, dont 2 endémiques.', null, null, null, false);
