INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (83016, 1000, 'TEXT', 'science.text', 'La forêt sèche, ou sclérophylle, est l’écosystème le plus menacé de disparition en Nouvelle-Calédonie. Pour informer, faciliter et encourager les actions de protection et de restauration écologique, un tableau de bord des forêts sèches de Nouvelle-Calédonie est en ligne depuis début 2021. ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (83017, 1000, 'TEXT', 'carousel.carouselElements.0.title', 'Panorama des programmes de sciences participatives ultramarins : les résultats', null, null, null, false);
