INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (83022, 1000, 'TEXT', 'testimony.quote', 'Dans le cadre du projet de Compteur, le premier état des lieux des programmes de sciences participatives dans les outre-mer a été mené. Consultez [la synthèse des résultats](media/article/Synthèse%20étude%20sciences%20participatives). ', null, null, null, false);
