INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (83026, 1000, 'TEXT', 'presentation.text', 'Les territoires d’outre-mer français présentent une biodiversité particulièrement riche et variée, mais fragilisée par les activités humaines.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (35068, 1017, 'TEXT', 'answer', 'Les écogestes, ce sont tous ces changements de comportement individuels qui permettent de réduire son impact sur l’environnement. Faire attention à son utilisation énergétique, faire les bons choix de consommation, mieux gérer ses déchets, consommer moins de viande, etc. des plus petits gestes aux grands changements, chacun est acteur de la préservation de l’environnement et peut limiter son impact. La multiplication de ces comportements individuels peut entraîner des changements notables à plus grande échelle !
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (83027, 1000, 'TEXT', 'carousel.carouselElements.4.text', 'Consultez les écogestes et adaptez vos habitudes pour réduire votre impact sur la biodiversité.', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (83028, 1000, 'CHECKBOX', 'carousel.carouselElements.4.shadowed', 'true', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (83029, 1000, 'TEXT', 'carousel.carouselElements.1.text', 'La structure guadeloupéenne *An ba loup-la* (« sous la loupe » en créole antillais) anime le programme *Ti bet a limiè*, l’Observatoire des lucioles de Guadeloupe. L’objectif ? Recueillir des données sur ces insectes méconnus, mais aussi sensibiliser le public au rôle écologique des insectes et aux perturbations des milieux dont ils sont victimes.', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (83030, 1000, 'IMAGE', 'carousel.carouselElements.3.image', null, 1832, ' Rainette de Gaucher (*Dendropsophus gaucheri*), Guyane © Raphael Gailhac - Office français de la biodiversité', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (83031, 1000, 'TEXT', 'presentation.indicators', 'Des chiffres clés pour comprendre la biodiversité', null, null, null, false);
