INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (83033, 1000, 'TEXT', 'carousel.carouselElements.4.title', 'Tous acteurs de la préservation de la biodiversité', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (83034, 1000, 'LINK', 'science.more', 'Lire l''article', null, null, 'media/article/Tableau%20de%20bord%20forêts%20sèches', false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (83035, 1000, 'CHECKBOX', 'carousel.carouselElements.1.shadowed', 'true', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (83036, 1000, 'TEXT', 'carousel.carouselElements.3.text', 'Dans les territoires d’outre-mer, 20 % des espèces sont considérées comme éteintes ou en danger d’extinction selon la Liste rouge des espèces menacées. Comment ce chiffre est-il calculé ? Quelles sont les menaces qui pèsent sur les espèces ? Quelles actions sont mises en place pour les préserver ? ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (83037, 1000, 'TEXT', 'carousel.carouselElements.0.text', 'Rapporter des observations de requins ou de raies, surveiller l’état de santé de coraux, ou encore compter des lucioles: autant d’activités qui composent les Sciences Participatives dans les Outre-mer. Un premier panorama, permettant l’inventaire et la caractérisation de ces activités vient d’être publié, nous en proposons ici une synthèse.  ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (83038, 1000, 'TEXT', 'presentation.territories', 'Découvrez l’identité de chaque territoire d’outre-mer', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (83039, 1000, 'TEXT', 'carousel.carouselElements.2.text', 'Naviguez parmi les portraits biodiversité des territoires pour mieux comprendre les enjeux avec une sélection de repères et de chiffres clés sur la nature.', null, null, null, false);
