INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81626, 1007, 'IMAGE', 'goal2.quoteImage', null, 1433, 'Requins pointe noire, Fakarava, Polynésie française © Franck Mazeas - 97px', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81627, 1007, 'TEXT', 'goal2.description', 'De nombreuses actions locales sont mises en place afin de tenter de préserver les écosystèmes et les espèces. Donner de la visibilité aux acteurs locaux, partager les expériences et les réussites, au bénéfice de tous, c’est également l’un des objectifs du Compteur. ', null, null, null, false);
