INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (83043, 1000, 'TEXT', 'presentation.quote', 'Les outre-mer regroupent 80 % de la biodiversité française, réinventons, adaptons notre façon de vivre et de voyager', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (83044, 1000, 'TEXT', 'carousel.carouselElements.2.title', 'Partez à la rencontre des espèces et des écosystèmes ultramarins', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (83045, 1000, 'TEXT', 'testimony.text', 'L''équipe du Compteur travaille avec ses partenaires sur le développement de nouveaux indicateurs pour enrichir les données disponibles. Des travaux sont également menés sur la perception de la nature et le rapport aux outils numériques. ', null, null, null, false);
