INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (83047, 1000, 'IMAGE', 'carousel.carouselElements.1.image', null, 1956, 'A la découverte des lucioles de Guadeloupe et de leur milieu de vie © Aline M. - An ba loup-la', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81075, 1038, 'TEXT', 'species.4.description', 'Deux espèces de loutres, la Loutre géante ([*Pteronura brasiliensis*](https://inpn.mnhn.fr/espece/cd_nom/443628)) et la Loutre commune ([*Lontra longicaudis*](https://inpn.mnhn.fr/espece/cd_nom/443627)) vivent dans les cours d’eau et sur les berges de Guyane. La première, de grande taille (jusqu’au 1,80 m de long), vit en groupes familiaux tandis que la seconde, plus petite, est souvent solitaire.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81076, 1038, 'TEXT', 'zoom.callToAction', 'Cliquez sur la carte thématique pour vous repérer en un coup d''œil ! ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81077, 1038, 'TEXT', 'ecosystems.ecosystems.0.name', 'La forêt humide', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81078, 1038, 'TEXT', 'species.5.description', 'Les plages de Guyane comptent parmi les plus importants sites de ponte de tortues marines au monde. Elles accueillent principalement trois espèces : la Tortue verte ([*Chelonia mydas*](https://inpn.mnhn.fr/espece/cd_nom/77338)), la Tortue olivâtre ([*Lepidochelys olivacea*]) et la Tortue luth ([*Dermochelys coriacea*](https://inpn.mnhn.fr/espece/cd_nom/77367)).
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81079, 1038, 'TEXT', 'species.6.description', 'Également nommé wassaï (ou açaï), le Palmier pinot ([*Euterpe olaracea*](https://inpn.mnhn.fr/espece/cd_nom/731659)) produit des fruits de couleur violet très foncé utilisés pour de nombreux usages (jus, huile, poudre). Ils poussent en grappes le long du tronc qui peut s’élever jusqu’à 20 m de hauteur, et sont également consommés par la faune sauvage. Ces palmiers sont particulièrement présents en zones marécageuses où ils peuvent former de véritables « pinotières ».
