INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81628, 1007, 'TEXT', 'goal3.description', 'Chacun, à son échelle, peut agir pour préserver la biodiversité. Connaitre et appliquer des gestes simples, modifier ses habitudes et son comportement ou encore s’investir dans des programmes de sciences participatives, tout le monde est concerné par la préservation de la biodiversité. Le rôle du Compteur est d’accompagner les citoyens en partageant des informations claires et adaptées à tous les territoires, et en encourageant à se mobiliser.', null, null, null, false);
