INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79540, 1039, 'TEXT', 'ecosystems.ecosystems.3.description', 'Plusieurs types de récifs se trouvent à Mayotte. Le lagon est entouré d’une barrière récifale de près de 140 km de long, large de 200 à 250 m et interrompue par douze passes. Elle est doublée par un récif interne sur 18 km au sud-ouest, phénomène rare dans le monde, et un récif frangeant longe les côtes. Les fonds sableux du lagon sont en partie colonisés par des herbiers de phanérogames.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79541, 1039, 'TEXT', 'ecosystems.ecosystems.4.description', 'Les herbiers sous-marins se trouvent tout autour de Grande Terre et des îlots, dans les zones côtières peu profondes à Mayotte. Peu denses et fragmentés, ils couvrent au total 760 hectares dans le lagon.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79542, 1039, 'TEXT', 'ecosystems.ecosystems.4.name', 'Les herbiers sous-marins ', null, null, null, false);
