INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79565, 1039, 'TEXT', 'identity.area', '377', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79566, 1039, 'TEXT', 'species.0.description', 'Le Crabier blanc ([*Ardeola idae*](https://inpn.mnhn.fr/espece/cd_nom/418710)) est un petit héron dont le plumage change de couleur au cours de l’année. En période de reproduction, il est reconnaissable à son plumage blanc immaculé, son bec bleu terminé par une pointe noire et ses pattes roses, tandis que le reste de l’année son plumage est marron strié de beige, son bec gris (toujours terminé d’une pointe noire) et ses pattes verdâtres. Il vit dans les zones humides où il se nourrit d’insectes, d’amphibiens et de petits poissons, et niche dans les mangroves.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79567, 1039, 'IMAGE', 'interests.locations.5.image', null, 1919, 'Vue aérienne du lac Dzaha © Alexandra Gigou - OFB', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79568, 1039, 'TEXT', 'identity.highestPointName', 'Mont Bénara', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79569, 1039, 'TEXT', 'ecosystems.ecosystems.2.name', 'Les mangroves ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79570, 1039, 'TEXT', 'species.7.description', 'On trouve sept espèces de palétuviers dans les mangroves de Mayotte. La plus commune est le palétuvier rouge, ([*Rhizophora mucronata*](https://inpn.mnhn.fr/espece/cd_nom/447468)), qui couvre 80 % des mangroves. Il se reconnait à ses racines en échasses et sa feuille lancéolée qui présente une pointe à son extrémité.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79571, 1039, 'TEXT', 'species.8.description', 'Le Namoulohna ([*Foetidia comorensis*](https://inpn.mnhn.fr/espece/cd_nom/807350)) est un arbre endémique de Mayotte, extrêmement rare&nbsp;: il n’est connu que d’une unique station de 600 m<sup>2</sup> avec environ 80 individus dénombrés en 2017. Connue depuis longtemps par les villageois locaux, elle n’a été décrite pour la science qu’en 2011. Menacée entre autres par les défrichements pour l’installation de cultures vivrières et le pâturage des animaux domestiques, l’espèce figure sur la liste des espèces protégée de Mayotte et un important travail de sensibilisation est mené autour de sa préservation.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79572, 1039, 'IMAGE', 'species.2.image', null, 1650, '*Eulemur fulvus* © Emmanuel Broeks', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73965, 1020, 'TEXT', 'species.10.description', 'Sur les quatre espèces de roussettes (chauves-souris frugivores et nectarivores) présentes en Nouvelle-Calédonie, trois en sont endémiques. La Roussette rousse ([*Pteropus ornatus*](https://www.endemia.nc/faune/fiche1328)) et celle du Pacifique ([*P. tonganus*](https://www.endemia.nc/faune/fiche1329)) sont des espèces-gibiers très prisées, dont la chasse est réglementée. Elles constituent aussi les proies des chats harets.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73966, 1020, 'TEXT', 'events.5.date', '2011', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81113, 1038, 'TEXT', 'ecosystems.ecosystems.0.description', 'La forêt humide tropicale couvre plus de 80 % du territoire de la Guyane et la diversité floristique y est exceptionnelle. Malgré une apparente uniformité, elle recèle de nombreuses variations de type en fonction de la prépondérance de certaines grandes familles botaniques et de facteurs physico-chimiques multiples, comme l’altitude, la nature du sol ou la pluviométrie.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81114, 1038, 'TEXT', 'identity.highestPoint', '851', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81115, 1038, 'TEXT', 'interests.locations.4.description', 'Sur la côte sud-est de la Guyane, cette zone de marais et forêts marécageuses est accessible uniquement par voie fluviale. On y trouve des espèces remarquables comme le [Caïman noir](https://inpn.mnhn.fr/espece/cd_nom/443974) et le [Lamantin](https://inpn.mnhn.fr/espece/cd_nom/821122/), et une très grande diversité d’oiseaux, dont le [Coq-de-roche](https://inpn.mnhn.fr/espece/cd_nom/442329/) et la [Harpie féroce](https://inpn.mnhn.fr/espece/cd_nom/441972). Cette vaste zone humide est classée Réserve naturelle nationale depuis 1998.', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81116, 1038, 'TEXT', 'risks.risks.0.name', 'Une biodiversité préservée, mais des impacts humains en expansion', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81117, 1038, 'IMAGE', 'interests.locations.1.image', null, 1762, 'Comptages d''oiseaux au Grand Connétable © Réserve naturelle du Grand Connétable', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81118, 1038, 'IMAGE', 'ecosystems.ecosystems.1.image', null, 1618, 'Inselberg de la roche Koutou © Olivier Tostain - 97px', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81119, 1038, 'TEXT', 'interests.locations.3.description', 'Situées à l’extrémité nord-ouest la Guyane, le long du littoral à proximité de l’embouchure du fleuve Maroni, les plages d’Awala-Yalimapo sont l’un des sites les plus importants au monde pour la ponte des tortues marines, principalement pour les tortues luths, vertes et olivâtres. Ces plages sont protégées au sein de la Réserve naturelle nationale de l’Amana qui s’étend jusqu’à l’embouchure de la rivière Organabo et couvre 14 800 ha de plages, mangroves, marais et savanes. ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81120, 1038, 'TEXT', 'identity.title', 'La Guyane,
