INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81630, 1007, 'TEXT', 'partners.title', 'Partenariats et collaborations ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81631, 1007, 'TEXT', 'goal1.description', 'Quelles sont les espèces présentes dans ce territoire ? Comment évoluent leurs populations ? Quel est l’état de santé des récifs coralliens ? Pour répondre à ces questions et bien d’autres, différents paramètres, appelés « indicateurs », sont mesurés par les scientifiques pour suivre l’évolution de l’état de la biodiversité. Le Compteur a pour mission de rendre compte de l’état de ces connaissances dans les territoires en s''appuyant notamment sur l''[ONB](https://naturefrance.fr/), l''[INPN](https://inpn.mnhn.fr/accueil/index) et les observatoires locaux. ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81632, 1007, 'IMAGE', 'goal3.image', null, 1431, ' Randonneurs © Observatoire Martiniquais de la biodiversité', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81633, 1007, 'TEXT', 'header.subtitle', 'C’est le Livre Bleu outre-mer, synthèse des travaux des Assises des outre-mer, qui propose en 2018 la création d’un Compteur de la biodiversité. Ce nouveau dispositif vient notamment enrichir le portail [Naturefrance](https://naturefrance.fr/) en présentant un focus sur les territoires ultramarins. Outil actualisé et pérenne, il favorisera la rencontre, la synergie des citoyens et des acteurs de la biodiversité.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81634, 1007, 'TEXT', 'header.title', 'Pourquoi un Compteur de la biodiversité en outre-mer ?', null, null, null, false);
