INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73987, 1020, 'TEXT', 'interests.title', 'Lieux emblématiques', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73988, 1020, 'TEXT', 'species.16.description', 'Le Corbeau calédonien ([*Corvus moneduloides*](https://www.endemia.nc/faune/fiche159)) est un oiseau commun, endémique, familier des forêts mais fréquentant également les milieux plus ouverts. Il est surtout réputé pour son intelligence, puisqu’il est capable de fabriquer et d’utiliser des outils, notamment pour attraper ses proies dans la cavité des arbres.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73989, 1020, 'TEXT', 'species.4.name', '*Amborella trichopoda*', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73990, 1020, 'TEXT', 'identity.area', '16 624', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73991, 1020, 'TEXT', 'species.0.description', '[*Sphaeropteris intermedia*](https://www.endemia.nc/flore/fiche106) est une espèce de fougère arborescente endémique parmi les plus grandes au monde, elle peut atteindre 35 mètres de haut. Commune sur le territoire, cette plante est utilisée en médecine traditionnelle et son tronc très solide est également utilisé pour la sculpture ou la confection de sagaies.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73992, 1020, 'TEXT', 'identity.highestPointName', 'Mont Panié', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73993, 1020, 'TEXT', 'ecosystems.ecosystems.2.name', 'Les maquis miniers', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73994, 1020, 'TEXT', 'ecosystems.ecosystems.5.name', 'Les herbiers de phanérogames marines', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73995, 1020, 'TEXT', 'species.7.description', 'Ce petit oiseau marin présente un dos gris, un ventre blanc, une tête pourvue d''une calotte noire et un bec droit et pointu. Une sous-espèce de la Sterne néreis, ([*Sternula nereis exul*](https://www.endemia.nc/faune/fiche52)) niche sur les îlots de Nouvelle-Calédonie, notamment dans le Lagon Sud, vers Koumac et aux îles Chesterfield. Cette sous-espèce est très menacée localement du fait de ses faibles effectifs de populations.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73996, 1020, 'TEXT', 'species.8.description', 'Le Niaouli ([*Melaleuca quinquenervia*](https://www.endemia.nc/flore/fiche445)), est un arbre utilisé en médecine et pour la construction, que l’on retrouve dans les milieux ouverts et anthropisés. Il se développe particulièrement bien dans les zones touchées par les incendies en raison de ses propriétés ignifuges. Indigène en Nouvelle-Calédonie, cette espèce a été introduite dans d’autres territoires comme la Guyane où elle présente un profil envahissant.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73997, 1020, 'IMAGE', 'species.13.image', null, 1325, '*Neogymnochrinus richeri* © Pierre Lozouet / IRD', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73998, 1020, 'IMAGE', 'species.2.image', null, 1240, '*Dugong dugong* © Hélène Udo', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73999, 1020, 'TEXT', 'species.11.name', 'La Baleine à bosse', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (74000, 1020, 'IMAGE', 'ecosystems.ecosystems.4.image', null, 1339, ' Mangrove © Christine Fort', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (74001, 1020, 'TEXT', 'species.3.description', 'Sur les huit espèces de Tricots rayés ([*Laticauda sp.*](https://www.endemia.nc/faune/fiche1104)) connues au niveau mondial, deux sont présentes en Nouvelle-Calédonie dont une endémique. Largement répandus sur le territoire, ces serpents marins se nourrissent dans l’eau mais reviennent sur terre pour se reposer, digérer et pondre. Bien que porteurs d''un venin mortel, ils ne sont pas agressifs et au contraire plutôt craintifs. De fait, les morsures sont très rares.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79577, 1039, 'TEXT', 'species.2.description', 'Ce lémurien est considéré comme une sous-espèce ou une variété du Lémur fauve de Madagascar ([*Eulemur fulvus*](https://inpn.mnhn.fr/espece/cd_nom/418673)). Il aurait été introduit par l’homme à Mayotte il y a plusieurs siècles. Il possède un pelage variant du beige au roux avec une tête noire et des yeux orangés. Vivant en groupe dans les zones forestières et se nourrissant principalement de fruits, la dégradation et la diminution de son habitat peuvent le pousser à s’approcher des zones habitées et consommer les productions fruitières ce qui crée des conflits avec les agriculteurs. L’espèce est protégée à Mayotte.
