INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (74002, 1020, 'TEXT', 'species.1.description', 'Emblème de la Nouvelle-Calédonie, le Cagou ([*Rhynochetos jubatus*](https://www.endemia.nc/faune/fiche144)), est un oiseau endémique qui ne vole pas mais court très vite, et dont le cri ressemble à l’aboiement d’un chien. Il vit dans les forêts humides de moyenne et haute altitude. Cette espèce est intégralement protégée.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (74003, 1020, 'TEXT', 'species.2.description', 'Le Dugong ([*Dugong dugong*](https://www.endemia.nc/faune/fiche5263)), aussi appelé « vache marine » est un mammifère marin herbivore qui passe la majeure partie de son temps à brouter les herbiers marins. Longtemps chassé et braconné, il est également vulnérable face à la dégradation des habitats marins et aux activités humaines. La population néo-calédonienne de dugongs est estimée à environ 700 individus. Un [plan d''action pour sa conservation](https://www.cen.nc/plan-actions-dugong) a été mis en place en 2010. Il est animé depuis 2017 par le Conservatoire d''espaces naturels de Nouvelle-Calédonie.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79606, 1039, 'TEXT', 'events.10.description', 'Création de la Réserve naturelle nationale des forêts de Mayotte', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (71996, 1043, 'TEXT', 'body.bodyElements.6.questionAnswer.response', 'Nous avons réalisé des protocoles de collecte simplifiés que les élèves mettent en place dans l’enceinte de l’école, avec l’enseignant. Les fourmis sont récupérées à l’aide d’une pince souple puis mises dans un tube contenant de l’éthanol dilué. Une fois prélevés, on les passe sous la loupe binoculaire pour une séance d’observation minutieuse. Pour les champignons, on collecte simplement un peu de terre dans un sachet. Les chercheurs se chargeront ensuite de déterminer les espèces présentes grâce à la détection d’ADN. Mais la pratique ne s’arrête pas là ! L’idée est que les élèves reproduisent l’échantillonnage chez eux, afin d’accroître de nombre d’échantillons dans toute la commune. Nous récupérons ensuite tous les échantillons pour analyse. **J’ai en ce moment en ma possession des organismes provenant de tout le territoire, dans des milieux très diversifiés.** Certains ont été prélevés dans de petites communes très isolées comme Camopi au bord fleuve, accessible qu’en pirogue il y a encore peu de temps. Il y a aussi les grandes villes comme Kourou, Saint-Laurent-du-Maroni ou Cayenne. Des villes côtières, forestières. Des maisons, des appartements de centre-ville. En quatre mois, nous avons reçu pas loin de 500 échantillons issus de 11 communes. **Grâce aux élèves nous allons découvrir une biodiversité encore inconnue en Guyane.**', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (71997, 1043, 'TEXT', 'presentation.name', '"Grâce aux élèves, nous allons découvrir une biodiversité encore inconnue en Guyane"', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79607, 1039, 'TEXT', 'interests.locations.0.description', 'Situé dans le lagon à l’est de Grande Terre, l’îlot Mbouzi est classé en Réserve naturelle nationale depuis 2007 afin de préserver sa relique de forêt sèche primaire, qui abrite des plantes remarquables et protégées dont le rare Ebène de Bernier ([*Diospyros bernieriana*](https://inpn.mnhn.fr/espece/cd_nom/967608)). On y trouve également la sous-espèce du Foudi des Comores endémique de Mayotte ([*Foudia eminentissima algondae*](https://inpn.mnhn.fr/espece/cd_nom/418756)). La réserve comprend une partie marine de 60 hectares qui abrite des communautés coralliennes remarquables. Elle est gérée par l’association les [Naturalistes de Mayotte](https://www.naturalistesmayotte.fr/).', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81640, 1007, 'IMAGE', 'goal1.image', null, 1427, 'Jaguar, Guyane © Raphaël Gailhac - OFB', null, false);
