INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81646, 1005, 'LINK', 'onb.link', 'Retrouvez les indicateurs sur le site de l’ONB', null, null, 'https://naturefrance.fr/indicateurs', false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81647, 1005, 'TEXT', 'inpn.description', 'L’INPN recense les espèces (animales, végétales et fongiques), les habitats naturels, les espaces protégés et le patrimoine géologique, en métropole et en outre-mer. L’ensemble des acteurs de l’environnement contribuent à la mise à jour régulière des informations sur le site de l’INPN dans le cadre du Système d’information de l’Inventaire national du patrimoine naturel (SINP). L’INPN met à disposition de tous des données de référence, validées par des réseaux d''experts. Le Muséum national d''Histoire naturelle en assure la responsabilité scientifique.', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79608, 1039, 'TEXT', 'risks.risks.2.description', 'Les inventaires naturalistes de la flore de Mayotte sont récents, le territoire n’ayant fait l’objet que de quelques prospections ponctuelles jusqu’au début des années 1990. Les travaux se sont intensifiés par la suite, avec notamment l’étendue de l’agrément du Conservatoire botanique national de Mascarin à Mayotte en 2007 et l’ouverture de l’antenne de Coconi en 2009.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81648, 1005, 'IMAGE', 'image', null, 1016, 'Serpent liane perroquet, *Leptophis ahaetulla*, Guyane © Raphaël Gailhac - OFB', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81649, 1005, 'TEXT', 'questions.1.quote', 'Avec ses territoires d’outre-mer, la France a donc une forte responsabilité dans la conservation de la biodiversité mondiale.', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81650, 1005, 'TEXT', 'questions.2.answer', 'De nombreux scientifiques alertent sur la crise de biodiversité actuelle, qui pourrait être la 6<sup>e</sup> extinction de masse à l’échelle des temps géologiques. En effet, le rythme actuel des disparitions d’espèces observé est 100 à 1000 fois supérieur au taux naturel de disparition ! On estime que près de la moitié des espèces vivantes pourrait disparaitre d’ici un siècle.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (74004, 1020, 'TEXT', 'species.9.description', '[*Syzygium acre*](https://www.endemia.nc/flore/fiche1027) est un arbre cauliflore principalement observé dans le sud de la Grande Terre. Ses feuilles de très grande taille – parfois plus d’un mètre de long – sont rouge à mauve lorsqu’elles sont juvéniles. Sa floraison est caractéristique : les fleurs, d’une coloration rouge à rose, se trouvent par petits groupes directement le long du tronc.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (71998, 1043, 'TEXT', 'body.bodyElements.4.questionAnswer.response', 'En Guyane c’est assez différent de la métropole. Nous n’avons pas la culture de la cueillette. **Il faut dire qu’hormis la chanterelle de Guyane il y a très peu de champignons destinés à l’alimentation**, ceux que nous mangeons proviennent de Chine ou d’Europe. **Les élèves découvrent généralement que les moisissures, omniprésentes ici en raison de la forte humidité, en font partie.** Quant aux fourmis, si elles accompagnent tout le monde au quotidien jusque dans les foyers, les connaissances varient fortement selon les écoles. A Camopi, village amérindien, les élèves se montrent beaucoup plus à l’aise. Les fourmis occupent une place importante dans leur culture. On utilise par exemple quelques grosses espèces comme les «&nbsp;balles de fusils&nbsp;» pour la vannerie, le tressage des feuilles. Lors du rituel du passage à l’âge adulte, le Maraké, on en dispose sur le corps du jeune qui doit résister aux piqûres. Ces enfants partent avec moins d’appréhension. Cela ne veut pas dire que les autres n’accrochent pas. La grande majorité des élèves finit par se prendre au jeu. La «&nbsp;chasse au fourmis&nbsp;» avec les enseignants provoquent une véritable excitation. Ils s’éclatent. Un élève a réussi à trouver une fourmi zombie dans la cour de l’école, on avait l’impression qu’il découvrait un trésor&nbsp;! Ce qui, vu sa rareté, en était un petit. Les fourmis tac-tac sont plus fréquentes, et font de la même manière l’objet d’une fascination partout où je me rends.', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (71999, 1043, 'IMAGE', 'body.bodyElements.3.image.imageElement', null, 1821, 'Intervention auprès des CM2 de l''école Augustine Duchange (Roura) © Marilou Hircq', null, false);
