INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (72002, 1043, 'TEXT', 'body.bodyElements.7.questionAnswer.question', 'Quel est l’avenir de ces données ? Que pourront nous apprendre tous ces prélèvements ?', null, null, null, false);
