INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (72005, 1043, 'TEXT', 'body.introduction', 'En Guyane, on ne s’intéresse pas qu’aux caïmans et aux jaguars ! Le projet BUG (Biodiversité Urbaine de Guyane) mobilise des centaines d’élèves depuis janvier pour inventorier les fourmis et les champignons. A l’école et chez eux.
