INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (74012, 1020, 'TEXT', 'species.4.description', 'Arbuste des forêts humides, [*Amborella trichopoda*](https://www.endemia.nc/flore/fiche254) est un taxon relique, considéré comme la plus ancienne des plantes à fleurs, son origine étant évaluée à 135 millions d’années avant notre ère. Elle est endémique de Nouvelle-Calédonie.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (74013, 1020, 'TEXT', 'zoom.callToAction', 'Cliquez sur la carte thématique pour vous repérer en un coup d''œil ! ', null, null, null, false);
