INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73411, 1031, 'TEXT', 'interests.locations.2.name', 'Le lagon de Wallis', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73412, 1031, 'TEXT', 'ecosystems.ecosystems.3.description', 'Le lagon de Wallis présente plusieurs formations récifales différentes et riches, tandis qu''Alofi ne possède qu’un lagon très restreint et que Futuna ne présente que des récifs frangeants. On trouve dans les eaux de Wallis-et-Futuna 135 espèces de coraux et plus de 700 espèces de poissons. ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73413, 1031, 'TEXT', 'ecosystems.ecosystems.4.description', 'Étendus à l’intérieur du lagon de Wallis, les herbiers sous-marins sont très importants dans l’équilibre de l’écosystème récifal et abritent de nombreuses espèces de mollusques, crustacés et juvéniles de poissons. On y trouve 3 espèces de phanérogames marines : [*Halodule pinifolia*](https://inpn.mnhn.fr/espece/cd_nom/643947), [*Halophila ovalis*](https://inpn.mnhn.fr/espece/cd_nom/446212) et [*Syringodium isoetifolium*](https://inpn.mnhn.fr/espece/cd_nom/627258).', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73414, 1031, 'TEXT', 'ecosystems.ecosystems.4.name', 'Les herbiers sous-marins', null, null, null, false);
