INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73456, 1031, 'TEXT', 'species.6.description', 'Ce terme peut désigner deux espèces d''arbres du genre Aglaia : [*A. samoaensis*](https://inpn.mnhn.fr/espece/cd_nom/889358
