INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73463, 1031, 'TEXT', 'events.2.date', '1300 - 900 avant JC', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79611, 1039, 'TEXT', 'interests.locations.1.description', 'Créé en janvier 2010, le [Parc naturel marin de Mayotte](https://www.parc-marin-mayotte.fr/) englobe le lagon et l’ensemble de la zone économique exclusive (ZEE), soit près de 70 000 kms<sup>2</sup>. Il est géré par l’Office français de la biodiversité. L''objectif du Parc est de concilier la préservation de la biodiversité marine et le développement durable des activités maritimes à Mayotte. Parmi leurs activités, les agents du parc veillent à faire respecter les règlementations de pêche, sensibiliser la population et les élus et réaliser des études et des suivis pour mieux connaitre le milieu marin. ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79612, 1039, 'TEXT', 'ecosystems.ecosystems.3.name', 'Les récifs et le lagon', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79613, 1039, 'TEXT', 'identity.subtitle', 'De nombreuses espèces terrestres endémiques', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79614, 1039, 'IMAGE', 'species.7.image', null, 1656, '*Bruguiera gymnorhiza* © Vincent Boullet', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79615, 1039, 'TEXT', 'species.5.name', 'Les dauphins', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79616, 1039, 'TEXT', 'risks.risks.0.description', 'Mayotte présente la densité de population la plus forte de tous les territoires d’outre-mer, et la population ne cesse d’augmenter. Cette pression démographique entraine de nombreuses conséquences sur les milieux naturels.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79617, 1039, 'TEXT', 'ecosystems.ecosystems.2.description', 'Forêt à l’interface entre le milieu marin et le milieu terrestre, dans la zone de balancement des marées, les mangroves couvrent environ 700 hectares répartis sur près de 120 sites le long du littoral mahorais. Zone de nidification pour les oiseaux, de nourrissage et de reproduction pour de nombreuses espèces (crabes, poissons, reptiles…), les mangroves limitent l’érosion et l’envasement du lagon, et permettent l’épuration de l’eau.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79618, 1039, 'TEXT', 'ecosystems.ecosystems.1.description', 'Ce terme, qui signifie « mauvaise terre » désigne des zones de sols dégradés et de roche nue, bruns ou rougeâtres, décapés par l’érosion et dépourvus de végétation. Ce phénomène naturel est largement amplifié par les activités humaines (déforestation, brulis, surpâturages). En 2010, les padzas couvraient 1 100 hectares soit 3 % du territoire.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79619, 1039, 'TEXT', 'interests.locations.5.description', 'Ce lac de cratère est la seule étendue d’eau naturelle de l’île de Petite Terre. D’une superficie de 23 ha, ses eaux de couleur verte sont uniquement peuplées de microorganismes (cyanobactéries, protozoaires…) en raison de conditions de vie particulièrement rudes : salinité plus élevée que l’eau de mer, eaux alcalines (ph entre 9 et 9,5), température de 30°C et surface sursaturée en oxygène mais anoxie au-delà d’1,5 m de profondeur. Il est bordé par des vestiges de forêt sèche.', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79620, 1039, 'TEXT', 'ecosystems.ecosystems.0.description', 'Les forêts de Mayotte ont été largement perturbées par les activités humaines, et il ne subsiste que quelques reliques de forêts primaires. Sur les monts et les crêtes, la forêt est de type tropical humide, avec un sous-bois dense et de nombreuses mousses, fougères, lianes et épiphytes (végétaux utilisant d’autres plantes comme support). Il existe également des reliquats de forêts primaires sèches comme sur l’îlot MBouzi.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79621, 1039, 'TEXT', 'identity.highestPoint', '660', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79622, 1039, 'TEXT', 'interests.locations.4.description', 'Au sud-ouest de la Grande Terre, la Baie de Bouéni abrite la plus vaste mangrove de Mayotte. Celle-ci s’étale sur près de 200 hectares et les 7 espèces de palétuviers présents à Mayotte y sont représentés. Cette mangrove abrite une faune nombreuse, dont le Crabier blanc ([*Ardeola idae*](https://inpn.mnhn.fr/espece/cd_nom/418710)), oiseau en danger critique d’extinction. Les formations récifales de la baie sont également très riches. Le site est reconnu comme zone nationale d’intérêt écologique, floristique et faunistique (ZNIEFF).', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79623, 1039, 'TEXT', 'risks.risks.0.name', 'La biodiversité de Mayotte sous pression ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79624, 1039, 'IMAGE', 'interests.locations.1.image', null, 1658, 'Vue aérienne du lagon et de la barrière récifale © Alexandra Gigou / Office français de la biodiversité', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79625, 1039, 'IMAGE', 'ecosystems.ecosystems.1.image', null, 1643, 'Padzas sur le Mont Choungui © Manuel Parizot', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79626, 1039, 'TEXT', 'interests.locations.3.description', 'Cette lagune littorale saumâtre située au nord-ouest de Petite Terre est entourée de tombolos (cordons de sédiments) au nord et au sud, percés par des brèches permettant la circulation de l''eau avec le lagon, selon le cycle des marées. Ses fonds vaseux sont peuplés d’herbiers de phanérogames que viennent brouter les tortues et les abords du plan d’eau sont colonisés par la mangrove. Le site accueille une faune riche, dont 35 espèces d’oiseaux. Il est reconnu zone humide d’importance internationale au titre de la Convention de RAMSAR depuis 2011.', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79627, 1039, 'TEXT', 'identity.title', 'Mayotte,
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79628, 1039, 'IMAGE', 'species.6.image', null, 1652, '*Chelonia mydas* © Alexandra Gigou / Office français de la biodiversité', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79629, 1039, 'TEXT', 'interests.locations.1.name', 'Le Parc naturel marin', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79630, 1039, 'TEXT', 'statistics.2.text', 'de mangrove', null, null, null, false);
