INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79632, 1039, 'TEXT', 'events.1.date', '-7 000 ans ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79633, 1039, 'TEXT', 'interests.locations.4.name', 'La Baie de Bouéni', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80631, 1010, 'TEXT', 'understand.text2', 'Chaque espèce est évaluée par un groupe d’experts selon des critères bien définis tels que la taille de ses populations, son aire de distribution ou encore la disparition de son habitat naturel, pour pouvoir lui attribuer une catégorie.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73464, 1031, 'TEXT', 'identity.presentation', 'A l’est de la Nouvelle-Calédonie, trois îles aux profils très différents composent le territoire français le plus éloigné de la métropole : l’archipel de Wallis-et-Futuna. L’île de Wallis, aussi appelée *''Uvea*, est entourée par un vaste lagon et des îlots coralliens et concentre les 2/3 de la population. A 230 km à l’ouest se trouvent les îles Horn séparées par un détroit : Futuna, au relief escarpé, et Alofi, quasiment non-habitée et encore largement recouverte de forêt primaire. La connaissance de la biodiversité de l’archipel est encore assez incomplète mais a beaucoup progressé ces dernières années.', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73465, 1031, 'TEXT', 'identity.population', '11 558', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73466, 1031, 'TEXT', 'species.3.name', 'La Roussette du Pacifique ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73467, 1031, 'TEXT', 'risks.risks.2.name', 'Préserver la biodiversité de Wallis-et-Futuna', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73468, 1031, 'IMAGE', 'species.8.image', null, 1569, '*Heliotropium foertherianum* © J-Y. H. Meyer, Délégation à la Recherche de la Polynésie française', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73469, 1031, 'TEXT', 'species.0.name', 'La Gallicolombe de Stair ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73470, 1031, 'TEXT', 'events.4.date', '1961', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73471, 1031, 'IMAGE', 'interests.locations.2.image', null, 1530, 'Lagon et îlots au nord de Wallis © Tristan Berr', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73472, 1031, 'IMAGE', 'ecosystems.ecosystems.2.image', null, 1526, 'Mangrove à Faioa © Enelio Liufau', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73473, 1031, 'TEXT', 'interests.locations.0.description', 'Le plus grand et le plus profond des cinq lacs de Wallis est issu de l’effondrement d’une ancienne caldeira. Il s’étend sur 400 m de diamètre et 80 m de profondeur et est entouré de parois verticales de plus de 30 m de hauteur. La forêt aux abords du lac, qui abrite de nombreux oiseaux et des roussettes, était autrefois protégée par un *Vao Tapu*, c’est-à-dire considérée comme taboue ou sacrée. ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73474, 1031, 'TEXT', 'risks.risks.2.description', 'L’archipel dispose d’un Code de l’environnement depuis 2007 et d’une Stratégie biodiversité depuis 2016. Le territoire ne compte pas d’aires protégées sur sens strict pour le moment, mais des réserves coutumières dites zones *Tapu* ont autrefois existé, par exemple au lac Lalolalo. Des volontés de créer des zones protégées existent, notamment concernant Alofi.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80632, 1010, 'TEXT', 'understand.text1', 'L''Union internationale pour la conservation de la nature (UICN) développe depuis 1964 un outil de référence permettant de mesurer l''état de conservation de la faune et de la flore mondiale : la Liste rouge. Les espèces y sont classifiées par catégories selon leur degré de vulnérabilité. Cela permet d''identifier les priorités d''action, les progrès accomplis ou les défis à relever pour la conservation des espèces.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80633, 1010, 'TEXT', 'territories.title', 'Pourcentage d''espèces menacées', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80634, 1010, 'CHECKBOX', 'presentation.shadowed', 'false', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80635, 1010, 'TEXT', 'visual.subtitle', 'Visualisez quelques exemples de pourcentages d''espèces menacées par groupe taxonomique', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80636, 1010, 'TEXT', 'understand.title1', 'La Liste rouge de l''UICN', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80637, 1010, 'TEXT', 'understand.paragraphs.1.text', 'Selon l''IPBES*, les cinq facteurs directs de changement qui affectent la nature et qui ont les plus forts impacts à l’échelle mondiale sont, dans l''ordre : les changements d’usage des terres et de la mer, l''exploitation directe de certains organismes, le changement climatique, les pollutions et les espèces exotiques envahissantes. Les impacts de ces différents facteurs sont souvent liés et s''additionnent : l''installation de certaines espèces exotiques peut par exemple être facilitée par les changements climatiques. Dans les îles, les espèces exotiques envahissantes seraient le premier facteur de perte de biodiversité.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80638, 1010, 'TEXT', 'understand.title2', 'Plusieurs catégories de risque d’extinction', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80639, 1010, 'TEXT', 'presentation.descriptionTerritories', 'des espèces sont considérées comme éteintes ou menacées', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80640, 1010, 'TEXT', 'understand.paragraphs.0.title', 'Encore beaucoup d''évaluations à réaliser', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80641, 1010, 'TEXT', 'understand.keyword', 'menaces', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81653, 1005, 'TEXT', 'questions.1.answer', 'La majorité des territoires d’outre-mer français sont situés dans des régions particulièrement riches en espèces, notamment en espèces endémiques, ce qu’on appelle des points chauds de la biodiversité (« Hotspots » en anglais) (Nouvelle-Calédonie, Antilles, Mayotte, La Réunion, Wallis et Futuna, Polynésie française). Avec la Guyane, la France possède une partie du plus grand massif forestier de la planète, l’Amazonie, et les territoires français réunissent 10 % des récifs coralliens répartis dans trois océans. Certains territoires isolés comme les îles subantarctiques abritent une faune et une flore adaptées à des climats extrêmes. ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81654, 1005, 'TEXT', 'inpn.title', 'L''Inventaire national du patrimoine naturel', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81655, 1005, 'TEXT', 'questions.2.question', 'La biodiversité menacée', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81656, 1005, 'TEXT', 'onb.description', 'Depuis 2012, l’Observatoire national de la biodiversité, actuellement piloté par l’Office français pour la biodiversité (OFB), publie des indicateurs sur l’état de la biodiversité française, avec la contribution de nombreux partenaires. Ils sont régulièrement actualisés et enrichis.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80642, 1010, 'TEXT', 'ecogestures.title', 'Que puis-je faire pour protéger les espèces menacées ?', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81657, 1005, 'TEXT', 'understand.description', 'La conservation de la biodiversité dépend de sa connaissance et de la compréhension des phénomènes qui l’impactent. Les indicateurs sont développés à partir de données de suivis et de mesures sur le terrain, et permettent de suivre l’évolution de l’état de santé de la biodiversité et de l’environnement. Ils sont utilisés pour définir des stratégies de gestion ou encore mesurer l''efficacité des plans de gestion d''espèces menacées.  Cependant, ils connaissent des limites par manque de données disponibles, manque de moyens de recherche ou parce que certains paramètres sont très complexes à évaluer.', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81658, 1005, 'TEXT', 'title', 'Compter la biodiversité : oui, mais comment ? ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80643, 1010, 'IMAGE', 'presentation.image', null, 1041, 'Albatros hurleur, archipel de Crozet, TAAF © Julie Tucoulet', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80644, 1010, 'TEXT', 'understand.paragraphs.1.title', 'Quelles menaces pèsent sur les espèces ?', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80645, 1010, 'IMAGE', 'visual.image', null, 1933, 'Carte espèces menacées outre-mer', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80646, 1010, 'TEXT', 'visual.title', 'Les espèces menacées en outre-mer', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80647, 1010, 'LINK', 'understand.article', 'aucun', null, null, 'aucun', false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80648, 1010, 'TEXT', 'understand.paragraphs.0.text', 'De nombreuses espèces ne sont pas encore évaluées ou sont classées dans la catégorie « données insuffisantes ». C’est le cas par exemple pour de nombreux invertébrés, les champignons, ou encore les lichens et mousses, qui sont pourtant essentiels au bon fonctionnement des écosystèmes. Les espèces marines souffrent également d''un important déficit d''évaluations.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80649, 1010, 'IMAGE', 'understand.image', null, 1042, 'Tortue luth, Guyane © Raphael Gailhac', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80650, 1010, 'TEXT', 'understand.paragraphs.2.title', 'Des programmes de conservation pour les espèces les plus menacées', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80651, 1010, 'TEXT', 'understand.paragraphs.2.text', 'De nombreux programmes sont mis en place pour tenter de protéger les espèces menacées. Ils visent à organiser des suivis des populations, mettre en œuvre des actions favorables à la restauration de ces populations ou de leur habitat, informer le public et les élus et faciliter l''intégration de la protection de ces espèces dans les politiques publiques.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80652, 1010, 'TEXT', 'presentation.description', 'des espèces d’outre-mer sont considérées comme éteintes ou menacées', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80653, 1010, 'TEXT', 'presentation.logoUrl', 'https://naturefrance.fr/indicateurs/proportion-despeces-eteintes-ou-menacees-dans-la-liste-rouge-nationale', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81659, 1005, 'TEXT', 'onb.title', 'L’Observatoire national de la biodiversité', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (78552, 1046, 'TEXT', 'body.bodyElements.0.questionAnswer.question', 'A quoi ressemblent les insectes pollinisateurs en Martinique, région du globe à la fois tropicale et insulaire ?', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (78553, 1046, 'CHECKBOX', 'presentation.shadowed', 'false', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (78554, 1046, 'IMAGE', 'body.bodyElements.4.image.imageElement', null, 1829, 'Participants au programme *Bet a flé* © Eddy Dumbardon-Martial', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (78555, 1046, 'TEXT', 'interviewee.presentation', 'Eddy Dumbardon-Martial, naturaliste et spécialiste des diptères, membre de Martinique Entomologie', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (78556, 1046, 'TEXT', 'body.bodyElements.6.questionAnswer.response', 'Certaines relations très spécialisées sont emblématiques. La pollinisation des Passiflores (fruits de la passion) par exemple ne peut pas être réalisée par l’abeille mellifère, celle-ci étant incapable d’atteindre l’étamine de la fleur du fait de sa taille. Une seule abeille peut y accéder : le vonvon ([*Xylocopa fimbriata*](https://inpn.mnhn.fr/espece/cd_nom/754400)), un xylocope très célèbre chez nous. **Autrefois très commune, l’espèce a beaucoup régressé**. Une des raisons est liée à la disparition des maisons en bois sur l’île, dans lesquelles le vonvon construisait ses nids. Avec la modernisation de l’habitat et la généralisation de matériaux à base d’aluminium et de béton, il a «&nbsp;déserté&nbsp;» les villes pour se «&nbsp;réfugier&nbsp;» en forêt.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (78557, 1046, 'TEXT', 'presentation.name', '« Il faut amener les gens à s’intéresser aux insectes pollinisateurs de Martinique » ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (78558, 1046, 'TEXT', 'presentation.author', 'Hugo Struna', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (78559, 1046, 'TEXT', 'body.bodyElements.7.questionAnswer.question', 'Selon vous et les données dont vous disposez, comment les insectes évoluent, en particulier les pollinisateurs ? Observez-vous une régression significative comme sur le continent ?', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (78560, 1046, 'TEXT', 'body.bodyElements.3.questionAnswer.question', 'Vous avez lancé il y a deux ans un programme de science participative, visant justement à améliorer la connaissance des insectes pollinisateurs. Comment avez-vous procédé ?', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (78561, 1046, 'TEXT', 'presentation.date', '22/09/2021', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (78562, 1046, 'TEXT', 'body.bodyElements.1.questionAnswer.response', 'C’est très récent ! En Martinique, nous n’avons pas d’historique naturaliste aussi ancien qu’en Europe. Ici, il y a eu l’esclavage entre le 17<sup>e</sup> et le 19<sup>e</sup> siècle, ce qui n’a pas encouragé l’observation de la nature. Avec l’arrivée des premiers colons européens, les ecclésiastiques de l’époque ont entrepris les premiers inventaires. Mais hormis de rares exceptions, comme celle du père Pinchon qui nous a laissé 140 boîtes de plusieurs milliers d’insectes, notre région a surtout bénéficié des travaux des botanistes. **Le premier inventaire des abeilles en Martinique a eu lieu en 2006.** Aujourd’hui encore nous sommes peu à nous intéresser à cette biodiversité. Il y a encore du travail !', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81660, 1005, 'TEXT', 'presentation', 'Les indicateurs sont des outils qui permettent de décrire différentes composantes de la biodiversité et ainsi mesurer son évolution dans le temps. De nombreux paramètres sont étudiés : taille des populations, état de santé des écosystèmes, taux de pollutions, etc. Ils renseignent ainsi l’état de la biodiversité à un instant précis, mais également l’état des connaissances et des moyens scientifiques actuels. Avec ses collaborateurs, le Compteur vise aussi à développer de nouveaux indicateurs ou chiffres clés sur la biodiversité ultramarine, qui viendront peu à peu enrichir le dispositif.', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81661, 1005, 'LINK', 'understand.article', 'aucun', null, null, 'aucun', false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81662, 1005, 'TEXT', 'quote', 'Documenter et présenter l’évolution de la biodiversité, c’est permettre à chacun de prendre conscience des enjeux et des menaces.', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81663, 1005, 'CHECKBOX', 'shadowed', 'false', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73475, 1031, 'TEXT', 'risks.risks.1.description', 'Au moins 416 espèces de plantes ont été introduites dans l''archipel, notamment comme ornementales dans les jardins. Parmi elles, plus de 150 sont considérées naturalisées, c’est-à-dire qu’elles se reproduisent sans intervention humaine, ce qui est beaucoup compte tenu de la petite taille du territoire. Les perturbations anthropiques ou naturelles (feux, déboisement, cyclone, etc.) peuvent faciliter leur installation et leur prolifération au détriment des plantes indigènes.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73476, 1031, 'TEXT', 'interests.locations.2.description', 'Sur plus de 200 km<sup>2</sup> s’étend un ensemble varié et très riche de formations récifales et d’herbiers sous-marins, ainsi que 19 îlots boisés où se reproduisent des tortues marines et de nombreuses espèces d’oiseaux marins. La barrière récifale est interrompue par quatre passes : trois à l’ouest et une au sud. ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73477, 1031, 'TEXT', 'interests.locations.1.description', 'Au contraire de Wallis qui n’en possède aucune, le relief de Futuna est parcouru de plusieurs rivières qui abritent une faune d’eau douce dont plusieurs espèces de gobies endémiques, et sont pour le moment exemptes de poissons introduits.', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73478, 1031, 'TEXT', 'ecosystems.ecosystems.3.name', 'Les récifs coralliens', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73479, 1031, 'TEXT', 'identity.subtitle', 'Du lagon de Wallis aux forêts d''Alofi', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73480, 1031, 'IMAGE', 'species.7.image', null, 1568, '*Rhizophora samoensis* © J-Y. H. Meyer, Délégation à la Recherche de la Polynésie française', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73481, 1031, 'TEXT', 'species.5.name', 'Le *Suka*', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73482, 1031, 'TEXT', 'risks.risks.0.description', 'A Wallis, les milieux naturels ont été grandement modifiés par les activités humaines, en particulier les forêts qui sont très dégradées, remplacées par le *toafa*. Futuna et Alofi sont plus préservées et la biodiversité terrestre y est plus riche. A l’inverse, la biodiversité marine du vaste lagon de Wallis est plus importante que celles des récifs frangeants de Futuna. Cependant les connaissances sur la biodiversité de Wallis-et-Futuna sont encore largement lacunaires.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73483, 1031, 'TEXT', 'ecosystems.ecosystems.2.description', 'La mangrove est présente uniquement sur Wallis, essentiellement dans les petites anses vaseuses de la côte ouest où elle recouvre 36 hectares. Il s’agit d’une formation assez dense de 3-4m de hauteur environ, autour de deux espèces de palétuviers (*togo*), [*Bruguiera gymnorrhiza*](https://inpn.mnhn.fr/espece/cd_nom/809530
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73484, 1031, 'TEXT', 'ecosystems.ecosystems.1.description', 'Le mot *Toafa*, « désert » en wallisien, reflète la pauvreté floristique de cette lande due aux feux répétés, où se trouve principalement des fougères ([*Dicranopteris linearis*](https://inpn.mnhn.fr/espece/cd_nom/448035)).
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73485, 1031, 'TEXT', 'ecosystems.ecosystems.0.description', 'Si la forêt dense occupait autrefois la quasi-totalité des îles, elle a aujourd’hui quasiment disparu de Wallis, hormis dans quelques zones peu accessibles. Elle est mieux conservée sur Futuna en raison du relief accidenté, bien que grignotée peu à peu par le défrichement pour les cultures. Sur Alofi, elle est encore relativement bien préservée, mais fait malgré tout l’objet d’exploitation par les agriculteurs de Futuna.', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73486, 1031, 'TEXT', 'identity.highestPoint', '524', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73487, 1031, 'TEXT', 'risks.risks.0.name', 'Des pressions sur le lagon et les forêts ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81664, 1005, 'IMAGE', 'understand.image', null, 1362, 'Fakarava, Polynésie française © Franck Mazeas - 97px', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81665, 1005, 'TEXT', 'questions.0.question', 'Qu’est-ce que la biodiversité ?', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73488, 1031, 'IMAGE', 'interests.locations.1.image', null, 1605, 'Rivière de Futuna © Jairo Talalua', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73489, 1031, 'IMAGE', 'ecosystems.ecosystems.1.image', null, 1572, 'Lande à *Toafa* © Jean-Yves H. Meyer, Délégation à la Recherche de la Polynésie française', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73490, 1031, 'TEXT', 'interests.locations.3.description', 'La plus petite île du groupe n’est habitée à l’année que par une seule personne mais fréquentée quasiment quotidiennement par les agriculteurs de Futuna. D’origine corallienne, elle est majoritairement recouverte par une forêt relativement bien conservée, malgré un défrichement de plus en plus important pour les cultures et le bois de construction.', null, null, null, false);
