INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73500, 1021, 'TEXT', 'events.7.description', 'Découverte du premier site de nidification du [Pétrel noir de Bourbon](https://www.petrels.re/les-especes/petrel-noir-de-bourbon/) ([*Pseudobulweria aterrima*](https://inpn.mnhn.fr/espece/cd_nom/442280)), oiseau endémique encore mal connu', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81670, 1016, 'TEXT', 'science.project.title', 'TsiÔno, le réseau des observateurs du milieu marin mahorais', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81671, 1016, 'TEXT', 'header.title', 'Agir ensemble pour préserver la biodiversité remarquable des outre-mer', null, null, null, false);
