INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81673, 1016, 'IMAGE', 'science.project.image', null, 1345, 'Plongeur à Mayotte © Alexandra Gigou - OFB', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81674, 1016, 'TEXT', 'science.title', 'Les sciences participatives', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81675, 1016, 'TEXT', 'science.project.description', 'S’appuyer sur les observations de tous les usagers de la mer pour mieux comprendre le milieu marin mahorais, c’est l’objectif du programme TsiÔno, « J’ai vu » en shimaoré. Le projet vise à préciser les inventaires des espèces et leur répartition, suivre les habitudes de certains individus pour 6 espèces cibles (dont la Baleine à bosse et le Dugong) et alerter sur la présence de phénomènes anormaux, comme la prolifération d’espèces envahissantes ou le blanchissement de coraux. ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81676, 1016, 'TEXT', 'science.subtitle', 'Les sciences participatives permettent à tous les curieux de la nature, du débutant à l’expérimenté, de contribuer à l''amélioration des connaissances sur la biodiversité en fournissant aux scientifiques un grand nombre de données de terrain.', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81677, 1016, 'TEXT', 'ecogestures.title', 'Découvrez les écogestes', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81678, 1016, 'IMAGE', 'header.background', null, 1335, 'Agricultrices, Mayotte © Bertrand Fanonnel - 97px', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81679, 1016, 'TEXT', 'header.subtitle', 'Réfléchir à son impact, changer ses habitudes, participer à la progression des connaissances sur les espèces et les espaces, donner de son temps dans des actions de terrain : chacun, à son échelle, peut agir et s’investir pour la préservation de la biodiversité de son territoire.
