INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (83090, 1053, 'TEXT', 'body.titre_question.1.citation.citationElement', 'Qu’est-ce que les sciences participatives ?', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73517, 1021, 'IMAGE', 'species.1.image', null, 1299, '*Lalage newtoni* © Jean-Philippe Siblet', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (78564, 1046, 'TEXT', 'body.bodyElements.9.questionAnswer.response', 'Il faut amener gens à observer, c’est la meilleure façon de sensibiliser. Avant nous essayions de le faire en montrant des boîtes de papillons. C’était beau. C’était spectaculaire. Mais ça restait folklorique, selon moi. **Je vois bien que lorsque ces mêmes personnes observent les mêmes espèces vivantes, dans leur milieu naturel, elles s’ouvrent à un autre monde.** Elles touchent du doigt la diversité des formes vivantes et prennent conscience des relations complexes entre les espèces. Cela entraine généralement une réflexion sur l’impact de nos actions sur les écosystèmes.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (78565, 1046, 'TEXT', 'body.bodyElements.6.questionAnswer.question', 'Que sait-on des relations plantes-insectes en Martinique ? Y-a-t-il des phénomènes de coévolutions remarquables ? ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (78566, 1046, 'TEXT', 'body.bodyElements.0.questionAnswer.response', 'Contrairement à ce qu’on pourrait penser, ce n’est pas très éloigné de ce qu’on retrouve en métropole. Les principaux groupes y sont présents : les Hyménoptères, les Diptères, les Coléoptères, les Lépidoptères. Il y a tout de même quelques différences. **La première est la faible diversité spécifique. Les îles sont des territoires exigus, isolés, peu d’espèces ont pu s’y établir.** Contrairement à la Guyane par exemple, qui jouit d’une faune et d’une flore extrêmement abondante en raison de sa situation continentale. En revanche, les îles se distinguent par un endémisme très élevé mais qui reste variable selon les groupes d’insectes. **Sur 18 espèces d’abeilles connues de Martinique, 8 sont endémiques, en tous cas à notre connaissance.** Si la plupart de ces espèces sont présentes dans plusieurs îles de l’archipel, certaines ne vivent que sur une seule d’entre elles ! Nous avons une forte responsabilité à l’échelle du monde du fait de nos espèces endémiques. Mais au risque de vous décevoir, il y a peu d’espèces spectaculaires quand bien même nous serions dans une région tropicale. Les espèces communes sont même en moyenne plus petites que sur le continent, ceci étant lié à des ressources plus limitées.', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (78567, 1046, 'TEXT', 'body.bodyElements.10.paragraph.paragraphText', '**Pour en savoir plus**, consultez le site du projet [*Bet a flé*](https://www.betafle.fr) et suivez [Martinique Entomologie](https://www.facebook.com/martiniqueentomologie) sur Facebook. ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (78568, 1046, 'IMAGE', 'presentation.image', null, 1822, 'Vonvon (*Xylocopa fimbriata*) butinant des fleurs de Glisérya (*Gliricidia sepium*) © Eddy Dumbardon-Martial', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (78569, 1046, 'TEXT', 'body.bodyElements.9.questionAnswer.question', 'Comme évoqué plus haut, la sensibilisation au sort des insectes est une de vos missions. Quel peut être le rôle de la population dans la sauvegarde de la biodiversité en Martinique ? ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (78570, 1046, 'TEXT', 'body.bodyElements.1.questionAnswer.question', 'De quand datent les premiers inventaires d’insectes pollinisateurs en Martinique ?', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (78571, 1046, 'TEXT', 'body.bodyElements.2.citation.citationElement', '«&nbsp;Mobiliser les gens autour de ces questions n’est pas une mince affaire, d’autant que nous avons ici des relations plus conflictuelles avec les insectes, souvent assimilés à des vecteurs de maladies.&nbsp;»', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (83091, 1053, 'CHECKBOX', 'presentation.shadowed', 'false', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (83092, 1053, 'TEXT', 'body.titre_question.0.paragraph.paragraphText', 'Et l’enjeu est de taille ! Au moins 80% de la biodiversité française se situerait dans les territoires d’Outre-mer ! <strong> Un premier panorama, permettant l’inventaire et la caractérisation de ces activités de sciences participatives, vient d’être publié</strong>. Quelques 145 programmes de sciences participatives ont été identifiés. Nous vous proposons, ici, une synthèse de ce panorama pour en exposer les principales caractéristiques, mais aussi, les enjeux, difficultés et opportunités, liées au déploiement des Sciences Participatives dans les Outre-mer.', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (83093, 1053, 'TEXT', 'presentation.name', 'Panorama des programmes de sciences participatives ultramarins : les résultats ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (83094, 1053, 'TEXT', 'body.titre_question.5.citation.citationElement', 'Quelles sont les caractéristiques d’un programme de Sciences participatives dans les Outre-mer ?', null, null, null, false);
