INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73565, 1021, 'TEXT', 'identity.presentation', '« L’île intense » est située sur un point chaud de biodiversité et présente un fort taux d’endémisme. Les milieux naturels y sont relativement bien conservés par rapport aux autres îles des Mascareignes (Maurice et Rodrigues) en raison du relief escarpé. De nombreuses espèces ont toutefois disparu depuis l’arrivée des humains au 16<sup>e</sup> siècle sur une île alors inhabitée. Forêts humides, ravines et rivières, cirques et remparts, volcan, récif corallien... La Réunion présente une diversité d’écosystèmes et une forte volonté de les préserver.', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73566, 1021, 'TEXT', 'identity.population', '853 659', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73567, 1021, 'TEXT', 'species.3.name', 'Le Petit Molosse de La Réunion', null, null, null, false);
