INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (83097, 1053, 'TEXT', 'body.titre_question.13.citation.citationElement', 'Conclusions et recommandations principales du rapport', null, null, null, false);
